import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/alertCorrelation.ts.content:
import { storage } from "./storage";
import { ThreatIntelligenceService } from "./threatIntelligence";
import { type RawAlert, type InsertIncident } from "@shared/schema";
import { randomUUID } from "crypto";

export interface CorrelationRule {
  id: string;
  name: string;
  description: string;
  enabled: boolean;
  conditions: {
    severityThreshold?: string;
    alertCount?: number;
    timeWindow?: number; // minutes
    sourceTypes?: string[];
    keywordMatches?: string[];
  };
  actions: {
    createIncident: boolean;
    escalate: boolean;
    notifyAnalyst: boolean;
  };
}

export interface CorrelationResult {
  shouldCreateIncident: boolean;
  incidentSeverity: 'low' | 'medium' | 'high' | 'critical';
  incidentTitle: string;
  incidentDescription: string;
  confidence: number;
  relatedAlerts: RawAlert[];
  triggeredRules: CorrelationRule[];
  threatIntelligence?: {
    indicators: any[];
    riskScore: number;
    threats: any[];
    recommendations: string[];
  };
}

export class AlertCorrelationEngine {
  private static defaultRules: CorrelationRule[] = [
    {
      id: "critical-alert-rule",
      name: "Critical Alert Auto-Escalation",
      description: "Automatically create incidents for critical severity alerts",
      enabled: true,
      conditions: {
        severityThreshold: "critical"
      },
      actions: {
        createIncident: true,
        escalate: true,
        notifyAnalyst: true
      }
    },
    {
      id: "multiple-high-alerts",
      name: "Multiple High Severity Alerts",
      description: "Create incident when 3+ high severity alerts occur within 30 minutes",
      enabled: true,
      conditions: {
        severityThreshold: "high",
        alertCount: 3,
        timeWindow: 30
      },
      actions: {
        createIncident: true,
        escalate: false,
        notifyAnalyst: true
      }
    },
    {
      id: "ransomware-indicators",
      name: "Ransomware Activity Detection",
      description: "Detect potential ransomware based on file encryption patterns",
      enabled: true,
      conditions: {
        keywordMatches: ["encrypt", "ransom", "locked", "decrypt", "bitcoin", "payment"]
      },
      actions: {
        createIncident: true,
        escalate: true,
        notifyAnalyst: true
      }
    },
    {
      id: "privilege-escalation",
      name: "Privilege Escalation Pattern",
      description: "Detect privilege escalation attempts",
      enabled: true,
      conditions: {
        keywordMatches: ["privilege", "escalation", "admin", "root", "sudo", "elevation"]
      },
      actions: {
        createIncident: true,
        escalate: false,
        notifyAnalyst: true
      }
    },
    {
      id: "data-exfiltration",
      name: "Data Exfiltration Detection",
      description: "Detect potential data exfiltration activities",
      enabled: true,
      conditions: {
        keywordMatches: ["exfiltration", "transfer", "upload", "download", "copy", "steal"]
      },
      actions: {
        createIncident: true,
        escalate: true,
        notifyAnalyst: true
      }
    }
  ];

  static async correlateAlert(alert: RawAlert): Promise<CorrelationResult> {
    const recentAlerts = await this.getRecentAlerts(60); // Last 60 minutes
    const triggeredRules: CorrelationRule[] = [];
    let shouldCreateIncident = false;
    let incidentSeverity: 'low' | 'medium' | 'high' | 'critical' = 'medium';
    let confidence = 0.5;

    // Check each correlation rule
    for (const rule of this.defaultRules) {
      if (!rule.enabled) continue;

      const ruleTriggered = await this.evaluateRule(rule, alert, recentAlerts);
      if (ruleTriggered) {
        triggeredRules.push(rule);

        if (rule.actions.createIncident) {
          shouldCreateIncident = true;
        }

        // Determine severity based on rule type
        if (rule.id === "critical-alert-rule" || rule.id === "ransomware-indicators") {
          incidentSeverity = 'critical';
          confidence = Math.max(confidence, 0.9);
        } else if (rule.id === "data-exfiltration" || rule.id === "multiple-high-alerts") {
          incidentSeverity = 'high';
          confidence = Math.max(confidence, 0.8);
        } else {
          incidentSeverity = 'medium';
          confidence = Math.max(confidence, 0.6);
        }
      }
    }

    // Threat intelligence enrichment
    const threatEnrichment = await ThreatIntelligenceService.enrichAlert(alert);

    // Machine learning-based correlation (simplified)
    const mlScore = await this.calculateMLScore(alert, recentAlerts);
    confidence = Math.max(confidence, mlScore);

    // Factor in threat intelligence risk score
    if (threatEnrichment.riskScore > 5) {
      confidence = Math.max(confidence, threatEnrichment.riskScore / 10);

      if (threatEnrichment.riskScore >= 7 && !shouldCreateIncident) {
        shouldCreateIncident = true;
        incidentSeverity = 'critical';
      } else if (threatEnrichment.riskScore >= 4 && !shouldCreateIncident) {
        shouldCreateIncident = true;
        incidentSeverity = 'high';
      }
    }

    // If ML score is very high, create incident even if no rules triggered
    if (mlScore > 0.85 && !shouldCreateIncident) {
      shouldCreateIncident = true;
      incidentSeverity = 'high';
    }

    const incidentTitle = this.generateIncidentTitle(alert, triggeredRules);
    const incidentDescription = await this.generateIncidentDescription(alert, triggeredRules, confidence);

    return {
      shouldCreateIncident,
      incidentSeverity,
      incidentTitle,
      incidentDescription,
      confidence,
      relatedAlerts: [alert, ...recentAlerts.slice(0, 5)], // Include up to 5 related alerts
      triggeredRules,
      threatIntelligence: threatEnrichment.indicators.length > 0 ? threatEnrichment : undefined
    };
  }

  private static async evaluateRule(
    rule: CorrelationRule,
    alert: RawAlert,
    recentAlerts: RawAlert[]
  ): Promise<boolean> {
    const { conditions } = rule;

    // Check severity threshold
    if (conditions.severityThreshold) {
      const severityLevels = { 'low': 1, 'medium': 2, 'high': 3, 'critical': 4 };
      const alertSeverity = severityLevels[alert.severity as keyof typeof severityLevels] || 0;
      const threshold = severityLevels[conditions.severityThreshold as keyof typeof severityLevels] || 0;

      if (alertSeverity < threshold) {
        return false;
      }
    }

    // Check alert count in time window
    if (conditions.alertCount && conditions.timeWindow) {
      const relevantAlerts = recentAlerts.filter(a => 
        new Date(a.receivedAt).getTime() > Date.now() - (conditions.timeWindow! * 60 * 1000)
      );

      if (relevantAlerts.length < conditions.alertCount - 1) { // -1 because current alert counts
        return false;
      }
    }

    // Check source types
    if (conditions.sourceTypes && conditions.sourceTypes.length > 0) {
      const source = await storage.getSource(alert.sourceId);
      if (!source || !conditions.sourceTypes.includes(source.type)) {
        return false;
      }
    }

    // Check keyword matches
    if (conditions.keywordMatches && conditions.keywordMatches.length > 0) {
      const alertText = [
        alert.description || '',
        alert.type || '',
        JSON.stringify(alert.rawData)
      ].join(' ').toLowerCase();

      const hasKeywordMatch = conditions.keywordMatches.some(keyword =>
        alertText.includes(keyword.toLowerCase())
      );

      if (!hasKeywordMatch) {
        return false;
      }
    }

    return true;
  }

  private static async getRecentAlerts(minutes: number = 60): Promise<RawAlert[]> {
    const allAlerts = await storage.getRawAlerts();
    const cutoffTime = new Date(Date.now() - minutes * 60 * 1000);

    return allAlerts
      .filter(alert => new Date(alert.receivedAt) > cutoffTime)
      .sort((a, b) => new Date(b.receivedAt).getTime() - new Date(a.receivedAt).getTime());
  }

  private static async calculateMLScore(alert: RawAlert, recentAlerts: RawAlert[]): Promise<number> {
    // Simplified ML scoring based on various factors
    let score = 0.3; // Base score

    // Severity contribution
    const severityScores = { 'critical': 0.4, 'high': 0.3, 'medium': 0.2, 'low': 0.1 };
    score += severityScores[alert.severity as keyof typeof severityScores] || 0.1;

    // Frequency analysis - if similar alerts occurred recently
    const similarAlerts = recentAlerts.filter(a => 
      a.type === alert.type || 
      a.severity === alert.severity ||
      a.sourceId === alert.sourceId
    );

    if (similarAlerts.length > 2) {
      score += 0.2; // Pattern detected
    }

    // Time-based analysis - alerts during off-hours are more suspicious
    const hour = new Date(alert.receivedAt).getHours();
    if (hour < 6 || hour > 22) {
      score += 0.1; // Off-hours activity
    }

    // Source reputation (simplified)
    const source = await storage.getSource(alert.sourceId);
    if (source?.type === 'EDR' || source?.type === 'SIEM') {
      score += 0.1; // Higher trust in EDR/SIEM alerts
    }

    return Math.min(score, 1.0); // Cap at 1.0
  }

  private static generateIncidentTitle(alert: RawAlert, triggeredRules: CorrelationRule[]): string {
    if (triggeredRules.length > 0) {
      const primaryRule = triggeredRules[0];
      if (primaryRule.id === "ransomware-indicators") {
        return "Potential Ransomware Activity Detected";
      } else if (primaryRule.id === "data-exfiltration") {
        return "Possible Data Exfiltration Attempt";
      } else if (primaryRule.id === "privilege-escalation") {
        return "Privilege Escalation Activity Detected";
      } else if (primaryRule.id === "critical-alert-rule") {
        return `Critical Security Alert: ${alert.type || 'Unknown'}`;
      } else if (primaryRule.id === "multiple-high-alerts") {
        return "Multiple High-Severity Security Events";
      }
    }

    // Fallback title
    return `Security Incident: ${alert.type || 'Automated Detection'}`;
  }

  private static async generateIncidentDescription(
    alert: RawAlert,
    triggeredRules: CorrelationRule[],
    confidence: number
  ): Promise<string> {
    let description = `Automated incident created with ${(confidence * 100).toFixed(1)}% confidence.\n\n`;

    description += `**Primary Alert Details:**\n`;
    description += `- Type: ${alert.type || 'Unknown'}\n`;
    description += `- Severity: ${alert.severity || 'Unknown'}\n`;
    description += `- Description: ${alert.description || 'No description available'}\n`;
    description += `- Received: ${new Date(alert.receivedAt).toISOString()}\n\n`;

    if (triggeredRules.length > 0) {
      description += `**Triggered Correlation Rules:**\n`;
      triggeredRules.forEach(rule => {
        description += `- ${rule.name}: ${rule.description}\n`;
      });
      description += '\n';
    }

    // Add threat intelligence information if available
    const threatEnrichment = await ThreatIntelligenceService.enrichAlert(alert);
    if (threatEnrichment.indicators.length > 0) {
      description += `**Threat Intelligence Matches:**\n`;
      description += `- Risk Score: ${threatEnrichment.riskScore.toFixed(1)}/10\n`;
      threatEnrichment.indicators.forEach(indicator => {
        description += `- ${indicator.type}: ${indicator.value} (${indicator.severity}, ${indicator.source})\n`;
      });
      description += '\n';

      if (threatEnrichment.recommendations.length > 0) {
        description += `**Threat Intelligence Recommendations:**\n`;
        threatEnrichment.recommendations.forEach(rec => {
          description += `- ${rec}\n`;
        });
        description += '\n';
      }
    }

    description += `**Recommended Actions:**\n`;
    if (triggeredRules.some(r => r.actions.escalate)) {
      description += `- Immediate escalation recommended\n`;
    }
    description += `- Review alert details and raw data\n`;
    description += `- Investigate related systems and users\n`;
    description += `- Consider containment measures if confirmed malicious\n`;

    return description;
  }

  static async createIncidentFromCorrelation(
    correlationResult: CorrelationResult,
    primaryAlert: RawAlert
  ): Promise<string | null> {
    if (!correlationResult.shouldCreateIncident) {
      return null;
    }

    const incidentData: InsertIncident = {
      title: correlationResult.incidentTitle,
      description: correlationResult.incidentDescription,
      severity: correlationResult.incidentSeverity,
      status: 'open'
    };

    try {
      const incident = await storage.createIncident(incidentData);

      // Create initial action for the incident
      await storage.createAction({
        incidentId: incident.id,
        actionType: 'AUTOMATED_DETECTION',
        payload: {
          primaryAlertId: primaryAlert.id,
          confidence: correlationResult.confidence,
          triggeredRules: correlationResult.triggeredRules.map(r => r.id),
          relatedAlertIds: correlationResult.relatedAlerts.map(a => a.id)
        },
        performedBy: 'system'
      });

      console.log(`‚úÖ Auto-created incident ${incident.id}: ${incident.title}`);
      return incident.id;
    } catch (error) {
      console.error('‚ùå Failed to create incident from correlation:', error);
      return null;
    }
  }
}

server/alertDataLoader.ts.content:
import fs from 'fs';
import path from 'path';

export interface AlertDataset {
  crowdstrike: any[];
  email: any[];
  firewall: any[];
  sentinelone: any[];
}

export class AlertDataLoader {
  private static instance: AlertDataLoader;
  private alertData: AlertDataset = {
    crowdstrike: [],
    email: [],
    firewall: [],
    sentinelone: []
  };
  private isLoaded = false;

  static getInstance(): AlertDataLoader {
    if (!AlertDataLoader.instance) {
      AlertDataLoader.instance = new AlertDataLoader();
    }
    return AlertDataLoader.instance;
  }

  async loadAlertData(): Promise<void> {
    if (this.isLoaded) return;

    try {
      const dataPath = path.join(process.cwd(), 'attached_assets');

      // Load CrowdStrike alerts
      const crowdStrikeFile = path.join(dataPath, 'crowdstrike_alerts_labeled_1754556002025.json');
      if (fs.existsSync(crowdStrikeFile)) {
        const crowdStrikeData = JSON.parse(fs.readFileSync(crowdStrikeFile, 'utf8'));
        this.alertData.crowdstrike = Array.isArray(crowdStrikeData) ? crowdStrikeData : [];
        console.log(`‚úÖ Loaded ${this.alertData.crowdstrike.length} CrowdStrike alerts`);
      }

      // Load Email alerts
      const emailFile = path.join(dataPath, 'email_alerts_labeled_1754556002031.json');
      if (fs.existsSync(emailFile)) {
        const emailData = JSON.parse(fs.readFileSync(emailFile, 'utf8'));
        this.alertData.email = Array.isArray(emailData) ? emailData : [];
        console.log(`‚úÖ Loaded ${this.alertData.email.length} Email alerts`);
      }

      // Load Firewall alerts
      const firewallFile = path.join(dataPath, 'firewall_alerts_labeled_1754556002031.json');
      if (fs.existsSync(firewallFile)) {
        const firewallData = JSON.parse(fs.readFileSync(firewallFile, 'utf8'));
        this.alertData.firewall = Array.isArray(firewallData) ? firewallData : [];
        console.log(`‚úÖ Loaded ${this.alertData.firewall.length} Firewall alerts`);
      }

      // Load SentinelOne alerts
      const sentinelOneFile = path.join(dataPath, 'sentinelone_alerts_labeled_1754556002032.json');
      if (fs.existsSync(sentinelOneFile)) {
        const sentinelOneData = JSON.parse(fs.readFileSync(sentinelOneFile, 'utf8'));
        this.alertData.sentinelone = Array.isArray(sentinelOneData) ? sentinelOneData : [];
        console.log(`‚úÖ Loaded ${this.alertData.sentinelone.length} SentinelOne alerts`);
      }

      this.isLoaded = true;
      const totalAlerts = this.alertData.crowdstrike.length + 
                         this.alertData.email.length + 
                         this.alertData.firewall.length + 
                         this.alertData.sentinelone.length;
      console.log(`üéØ Total alerts loaded: ${totalAlerts}`);
    } catch (error) {
      console.error('‚ùå Error loading alert data:', error);
      this.isLoaded = false;
    }
  }

  getRandomAlerts(sourceType: string, count: number): any[] {
    const sourceKey = sourceType.toLowerCase() as keyof AlertDataset;
    const sourceData = this.alertData[sourceKey];

    if (!sourceData || sourceData.length === 0) {
      throw new Error(`No data available for source type: ${sourceType}`);
    }

    const results = [];
    for (let i = 0; i < count; i++) {
      const randomIndex = Math.floor(Math.random() * sourceData.length);
      const alert = { ...sourceData[randomIndex] };

      // Update timestamp to current time for realism
      if (alert.timestamp) {
        alert.timestamp = new Date(Date.now() - Math.random() * 3600000).toISOString();
      }

      results.push(alert);
    }

    return results;
  }

  getDatasetStats(): { [key: string]: number } {
    return {
      crowdstrike: this.alertData.crowdstrike.length,
      email: this.alertData.email.length,
      firewall: this.alertData.firewall.length,
      sentinelone: this.alertData.sentinelone.length,
      total: this.alertData.crowdstrike.length + 
             this.alertData.email.length + 
             this.alertData.firewall.length + 
             this.alertData.sentinelone.length
    };
  }

  getSampleAlert(sourceType: string): any | null {
    const sourceKey = sourceType.toLowerCase() as keyof AlertDataset;
    const sourceData = this.alertData[sourceKey];

    if (!sourceData || sourceData.length === 0) {
      return null;
    }

    const randomIndex = Math.floor(Math.random() * sourceData.length);
    return sourceData[randomIndex];
  }

  async ensureDataLoaded(): Promise<void> {
    if (!this.isLoaded) {
      await this.loadAlertData();
    }
  }
}

server/alertProcessor.ts.content:
import { storage } from "./storage";
import { AlertNormalizer, type NormalizedAlertData } from "./normalization";
import { AlertDataLoader } from "./alertDataLoader";
import { AlertCorrelationEngine } from "./alertCorrelation";
import { type RawAlert, type InsertRawAlert } from "@shared/schema";
import { randomUUID } from "crypto";

export interface AlertProcessingResult {
  rawAlert: RawAlert;
  normalizedData: NormalizedAlertData;
  success: boolean;
  error?: string;
  incidentCreated?: boolean;
  incidentId?: string;
  correlationConfidence?: number;
}

export class AlertProcessor {

  static async processIncomingAlert(
    alertData: any, 
    sourceId: string, 
    sourceType?: string
  ): Promise<AlertProcessingResult> {
    try {
      // Step 1: Normalize the incoming alert
      const normalizedData = AlertNormalizer.normalize(alertData, sourceType);

      // Step 2: Convert to raw alert format and store
      const rawAlertData = AlertNormalizer.toRawAlert(normalizedData, sourceId);
      const rawAlert = await storage.createRawAlert(rawAlertData);

      // Step 3: Run correlation analysis to determine if incident should be created
      const correlationResult = await AlertCorrelationEngine.correlateAlert(rawAlert);

      let incidentId: string | null = null;
      let incidentCreated = false;

      if (correlationResult.shouldCreateIncident) {
        incidentId = await AlertCorrelationEngine.createIncidentFromCorrelation(correlationResult, rawAlert);
        incidentCreated = incidentId !== null;

        if (incidentCreated) {
          console.log(`üö® Auto-created incident ${incidentId} for ${normalizedData.sourceType} alert`);
        }
      }

      console.log(`‚úÖ Processed ${normalizedData.sourceType} alert: ${normalizedData.title} (correlation: ${(correlationResult.confidence * 100).toFixed(1)}%)`);

      return {
        rawAlert,
        normalizedData,
        success: true,
        incidentCreated,
        incidentId: incidentId || undefined,
        correlationConfidence: correlationResult.confidence
      };
    } catch (error) {
      console.error('Alert processing failed:', error);
      return {
        rawAlert: {} as RawAlert,
        normalizedData: {} as NormalizedAlertData,
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error'
      };
    }
  }

  static async processBulkAlerts(
    alerts: Array<{ alertData: any; sourceId: string; sourceType?: string }>
  ): Promise<AlertProcessingResult[]> {
    const results: AlertProcessingResult[] = [];

    for (const alert of alerts) {
      const result = await this.processIncomingAlert(
        alert.alertData,
        alert.sourceId,
        alert.sourceType
      );
      results.push(result);
    }

    const successCount = results.filter(r => r.success).length;
    console.log(`‚úÖ Bulk processing complete: ${successCount}/${results.length} alerts processed successfully`);

    return results;
  }

  static async simulateIncomingAlerts(
    sourceType: string, 
    count: number = 5
  ): Promise<AlertProcessingResult[]> {
    // Load real alert data
    const dataLoader = AlertDataLoader.getInstance();
    await dataLoader.ensureDataLoaded();

    // First, find or create a source for this simulation
    const sources = await storage.getSources();
    let source = sources.find(s => s.type.toLowerCase() === sourceType.toLowerCase());

    if (!source) {
      source = await storage.createSource({
        name: `${sourceType} Real Data Simulator`,
        type: sourceType,
        config: { simulated: true, dataSource: 'real_alerts', endpoint: `https://api.${sourceType.toLowerCase()}.example.com` }
      });
    }

    try {
      // Use real alert data instead of generated samples
      const realAlerts = dataLoader.getRandomAlerts(sourceType, count);
      const results: AlertProcessingResult[] = [];

      console.log(`üéØ Simulating ${count} real ${sourceType} alerts...`);

      for (const alertData of realAlerts) {
        const result = await this.processIncomingAlert(alertData, source.id, sourceType);
        results.push(result);
      }

      const successCount = results.filter(r => r.success).length;
      console.log(`‚úÖ Successfully processed ${successCount}/${count} ${sourceType} alerts`);

      return results;
    } catch (error) {
      console.log(`‚ö†Ô∏è  No real data available for ${sourceType}, falling back to generated samples`);
      // Fallback to generated data if real data is not available
      const sampleAlerts = this.generateSampleAlerts(sourceType, count);
      const results: AlertProcessingResult[] = [];

      for (const alertData of sampleAlerts) {
        const result = await this.processIncomingAlert(alertData, source.id, sourceType);
        results.push(result);
      }

      return results;
    }
  }

  static async simulateRealTimeAlerts(
    sourceType: string,
    durationMinutes: number = 5,
    alertsPerMinute: number = 2
  ): Promise<void> {
    const dataLoader = AlertDataLoader.getInstance();
    await dataLoader.ensureDataLoaded();

    console.log(`üîÑ Starting real-time simulation: ${sourceType} alerts for ${durationMinutes} minutes (${alertsPerMinute}/min)`);

    const intervalMs = (60 * 1000) / alertsPerMinute; // Convert to milliseconds between alerts
    const totalAlerts = durationMinutes * alertsPerMinute;
    let processedCount = 0;

    const interval = setInterval(async () => {
      try {
        if (processedCount >= totalAlerts) {
          clearInterval(interval);
          console.log(`üèÅ Real-time simulation completed: ${processedCount} alerts processed`);
          return;
        }

        const result = await this.simulateIncomingAlerts(sourceType, 1);
        if (result[0]?.success) {
          processedCount++;
          console.log(`‚ö° Real-time alert ${processedCount}/${totalAlerts}: ${result[0].normalizedData.title}`);
        }
      } catch (error) {
        console.error('‚ùå Real-time simulation error:', error);
      }
    }, intervalMs);

    // Return immediately, simulation runs in background
    return;
  }

  static async getDatasetStats(): Promise<any> {
    const dataLoader = AlertDataLoader.getInstance();
    await dataLoader.ensureDataLoaded();
    return dataLoader.getDatasetStats();
  }

  static async getSampleAlert(sourceType: string): Promise<any> {
    const dataLoader = AlertDataLoader.getInstance();
    await dataLoader.ensureDataLoaded();
    return dataLoader.getSampleAlert(sourceType);
  }

  private static generateSampleAlerts(sourceType: string, count: number): any[] {
    const alerts = [];

    for (let i = 0; i < count; i++) {
      switch (sourceType.toLowerCase()) {
        case 'crowdstrike':
          alerts.push(this.generateCrowdStrikeSample(i));
          break;
        case 'email':
          alerts.push(this.generateEmailSample(i));
          break;
        case 'firewall':
          alerts.push(this.generateFirewallSample(i));
          break;
        case 'sentinelone':
          alerts.push(this.generateSentinelOneSample(i));
          break;
        default:
          throw new Error(`Unsupported source type: ${sourceType}`);
      }
    }

    return alerts;
  }

  private static generateCrowdStrikeSample(index: number) {
    const severities = ['Low', 'Medium', 'High', 'Critical'];
    const tactics = ['Defense Evasion', 'Execution', 'Persistence', 'Privilege Escalation'];
    const techniques = ['T1547', 'T1078', 'T1036', 'T1055'];
    const detections = ['RemoteAccessTool', 'CommandAndControl', 'CredentialAccess', 'Malware'];

    return {
      alert_id: randomUUID(),
      detect_id: randomUUID(),
      device_id: randomUUID(),
      device_name: `workstation-${String(index + 1).padStart(2, '0')}.corp.local`,
      agent_version: "4.2.8",
      sensor_version: "6.1.15",
      ip_address: `192.168.1.${100 + index}`,
      mac_address: `00:11:22:33:44:${String(50 + index).padStart(2, '0')}`,
      user_name: `user${index + 1}`,
      file_name: `suspicious_${index}.exe`,
      file_path: `C:\\Temp\\suspicious_${index}.exe`,
      file_hash_sha1: `a1b2c3d4e5f6789${index}`,
      file_hash_sha256: `def456789abc123${index}${'0'.repeat(48)}`,
      severity: severities[index % severities.length],
      tactic: tactics[index % tactics.length],
      technique: techniques[index % techniques.length],
      detect_name: detections[index % detections.length],
      command_line: `suspicious_${index}.exe --malware-flag`,
      parent_process: "explorer.exe",
      parent_cmdline: "C:\\Windows\\explorer.exe",
      timestamp: new Date(Date.now() - Math.random() * 3600000).toISOString(),
      event_platform: "Windows",
      event_type: "ProcessRollup",
      network_remote_ip: `203.0.113.${10 + index}`,
      network_remote_port: 8080 + index,
      protocol: "TCP",
      alert_label: "true_positive"
    };
  }

  private static generateEmailSample(index: number) {
    const verdicts = ['malicious', 'suspicious', 'clean'];
    const malwareTypes = ['Emotet', 'Dridex', 'None'];
    const engines = ['Microsoft Defender', 'Proofpoint', 'Mimecast'];

    return {
      alert_id: randomUUID(),
      sender_email: `suspicious${index}@badactor.com`,
      recipient_email: `user${index}@company.com`,
      subject: `Urgent Action Required - Case ${index + 1000}`,
      timestamp: new Date(Date.now() - Math.random() * 3600000).toISOString(),
      verdict: verdicts[index % verdicts.length],
      quarantine_status: index % 2 === 0 ? "blocked" : "quarantined",
      malware_detected: malwareTypes[index % malwareTypes.length],
      phishing_detected: index % 3 === 0,
      malicious_link_detected: index % 4 === 0,
      spam_score: Math.random() * 10,
      attachment_name: `document_${index}.pdf`,
      attachment_hash: `hash${index}${'0'.repeat(60)}`,
      message_id: randomUUID(),
      header_from: `noreply${index}@suspicious.com`,
      reply_to: `reply${index}@badactor.com`,
      detection_engine: engines[index % engines.length],
      message_size: 15000 + Math.floor(Math.random() * 50000),
      alert_label: "true_positive"
    };
  }

  private static generateFirewallSample(index: number) {
    const actions = ['allow', 'deny', 'drop', 'reset'];
    const protocols = ['TCP', 'UDP', 'ICMP'];
    const threatTypes = ['port-scan', 'brute-force', 'dos-attack', 'C2'];
    const severities = ['Low', 'Medium', 'High', 'Critical'];

    return {
      alert_id: randomUUID(),
      firewall_name: `fw-${String(index + 1).padStart(2, '0')}.company.com`,
      src_ip: `203.0.113.${10 + index}`,
      src_port: 50000 + index,
      dest_ip: `192.168.1.${100 + index}`,
      dest_port: [22, 80, 443, 3389][index % 4],
      protocol: protocols[index % protocols.length],
      action: actions[index % actions.length],
      rule_name: `rule_security_${index}`,
      policy_id: 1000 + index,
      policy_name: `policy_block_${index}`,
      application: ['SSH', 'HTTP', 'HTTPS', 'RDP'][index % 4],
      threat_type: threatTypes[index % threatTypes.length],
      severity: severities[index % severities.length],
      category: ['threat', 'application', 'network'][index % 3],
      timestamp: new Date(Date.now() - Math.random() * 3600000).toISOString(),
      interface: ['eth0', 'eth1', 'wan1'][index % 3],
      country: ['Unknown', 'China', 'Russia', 'North Korea'][index % 4],
      alert_label: "true_positive"
    };
  }

  private static generateSentinelOneSample(index: number) {
    const classifications = ['Ransomware', 'Trojan', 'PUA', 'Infostealer'];
    const verdicts = ['malicious', 'suspicious', 'benign'];
    const detections = ['Generic.Trojan.Downloader', 'Backdoor.Win32.DarkKomet', 'Win32/Filecoder.AA'];

    return {
      alert_id: randomUUID(),
      agent_uuid: randomUUID(),
      agent_ip: `192.168.1.${150 + index}`,
      agent_os_type: ['windows', 'linux', 'mac'][index % 3],
      agent_os_revision: String(60000 + index),
      agent_version: "5.2.1",
      agent_hostname: `endpoint-${String(index + 1).padStart(2, '0')}.corp.local`,
      site_name: `Site ${index + 1}`,
      group_name: ['IT Ops', 'Finance Dept', 'R&D'][index % 3],
      account_name: `account${index}`,
      threat_id: randomUUID(),
      threat_classification: classifications[index % classifications.length],
      detection_name: detections[index % detections.length],
      file_path: `C:\\Users\\user${index}\\AppData\\Temp\\malware_${index}.exe`,
      file_hash_sha256: `sentinel${index}${'0'.repeat(56)}`,
      file_hash_md5: `md5hash${index}${'0'.repeat(24)}`,
      file_hash_sha1: `sha1hash${index}${'0'.repeat(32)}`,
      process_name: `process_${index}.exe`,
      process_id: 1000 + index,
      parent_process_name: "explorer.exe",
      parent_process_id: 500 + index,
      verdict: verdicts[index % verdicts.length],
      initiated_by: ['user', 'system', 'remote'][index % 3],
      storyline_id: randomUUID(),
      mitigation_status: ['mitigated', 'pending', 'unresolved'][index % 3],
      indicator_type: ['Process', 'File', 'Network Connection'][index % 3],
      network_remote_ip: `203.0.113.${50 + index}`,
      network_remote_port: 9000 + index,
      network_protocol: protocols[index % protocols.length],
      timestamp: new Date(Date.now() - Math.random() * 3600000).toISOString(),
      alert_label: "true_positive"
    };
  }
}

const protocols = ['TCP', 'UDP', 'ICMP'];

server/analyticsService.ts.content:
import { storage } from "./storage";
import { type RawAlert, type Incident, type Action, type Feedback, type ModelMetric } from "@shared/schema";

export interface AnalyticsMetrics {
  // Model Performance
  precision: number;
  recall: number;
  accuracy: number;
  f1Score: number;

  // Response Times
  avgResponseTimeMinutes: number;
  avgDetectionTime: number;
  avgResolutionTime: number;

  // Volume Metrics
  alertsProcessedToday: number;
  alertsProcessedWeek: number;
  incidentsCreatedToday: number;
  incidentsResolvedToday: number;

  // Automation Metrics
  automationRate: number;
  falsePositiveRate: number;
  threatDetectionRate: number;
  escalationRate: number;

  // Analyst Performance
  avgFeedbackRating: number;
  analystProductivity: number;
  manualReviewsToday: number;

  // Trend Data
  weeklyTrends: {
    alerts: number[];
    incidents: number[];
    responseTime: number[];
  };

  // Severity Distribution
  severityDistribution: {
    critical: number;
    high: number;
    medium: number;
    low: number;
  };

  // Source Performance
  sourceMetrics: {
    sourceId: string;
    sourceName: string;
    alertCount: number;
    accuracy: number;
    falsePositiveRate: number;
  }[];
}

export class AnalyticsService {
  static async calculateRealTimeMetrics(): Promise<AnalyticsMetrics> {
    const [alerts, incidents, actions, feedback, metrics, sources] = await Promise.all([
      storage.getRawAlerts(),
      storage.getIncidents(),
      storage.getActions(),
      storage.getFeedback(),
      storage.getModelMetrics(),
      storage.getSources()
    ]);

    const now = Date.now();
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const todayTime = today.getTime();

    const weekAgo = new Date(now - 7 * 24 * 60 * 60 * 1000);

    // Model Performance Metrics
    const latestMetric = metrics[0];
    const precision = latestMetric?.precision || this.calculatePrecisionFromData(incidents, alerts);
    const recall = latestMetric?.recall || this.calculateRecallFromData(incidents, alerts);
    const accuracy = latestMetric?.accuracy || this.calculateAccuracyFromData(incidents, alerts);
    const f1Score = (2 * precision * recall) / (precision + recall) || 0;

    // Response Time Calculations
    const resolvedIncidents = incidents.filter(inc => inc.closedAt);
    const avgResponseTimeMinutes = resolvedIncidents.length > 0
      ? resolvedIncidents.reduce((sum, inc) => {
          const responseTime = new Date(inc.closedAt!).getTime() - new Date(inc.createdAt).getTime();
          return sum + responseTime;
        }, 0) / resolvedIncidents.length / (1000 * 60)
      : 0;

    // Detection time (time from alert to incident creation)
    const avgDetectionTime = this.calculateAvgDetectionTime(alerts, incidents);

    // Resolution time (time from incident creation to closure)
    const avgResolutionTime = resolvedIncidents.length > 0
      ? resolvedIncidents.reduce((sum, inc) => {
          const resolutionTime = new Date(inc.closedAt!).getTime() - new Date(inc.createdAt).getTime();
          return sum + resolutionTime;
        }, 0) / resolvedIncidents.length / (1000 * 60 * 60) // Convert to hours
      : 0;

    // Volume Metrics
    const alertsProcessedToday = alerts.filter(alert => 
      new Date(alert.receivedAt).getTime() >= todayTime
    ).length;

    const alertsProcessedWeek = alerts.filter(alert => 
      new Date(alert.receivedAt) >= weekAgo
    ).length;

    const incidentsCreatedToday = incidents.filter(inc => 
      new Date(inc.createdAt).getTime() >= todayTime
    ).length;

    const incidentsResolvedToday = incidents.filter(inc => 
      inc.closedAt && new Date(inc.closedAt).getTime() >= todayTime
    ).length;

    // Automation Metrics
    const automatedActions = actions.filter(action => 
      action.actionType === 'AUTOMATED_DETECTION' || 
      action.actionType === 'AUTO_QUARANTINE'
    );
    const automationRate = actions.length > 0 ? automatedActions.length / actions.length : 0;

    const falsePositiveRate = this.calculateFalsePositiveRate(incidents, feedback);
    const threatDetectionRate = this.calculateThreatDetectionRate(alerts, incidents);
    const escalationRate = this.calculateEscalationRate(incidents, actions);

    // Analyst Performance
    const validFeedback = feedback.filter(f => f.rating !== null);
    const avgFeedbackRating = validFeedback.length > 0
      ? validFeedback.reduce((sum, f) => sum + (f.rating || 0), 0) / validFeedback.length
      : 0;

    const manualActions = actions.filter(action => 
      action.performedBy !== 'system' && action.performedBy !== null
    );
    const analystProductivity = this.calculateAnalystProductivity(manualActions, incidents);

    const manualReviewsToday = actions.filter(action => 
      action.actionType === 'ANALYST_REVIEW' && 
      new Date(action.performedAt).getTime() >= todayTime
    ).length;

    // Weekly Trends
    const weeklyTrends = this.calculateWeeklyTrends(alerts, incidents);

    // Severity Distribution
    const severityDistribution = this.calculateSeverityDistribution(incidents);

    // Source Performance
    const sourceMetrics = await this.calculateSourceMetrics(sources, alerts, incidents);

    return {
      precision,
      recall,
      accuracy,
      f1Score,
      avgResponseTimeMinutes,
      avgDetectionTime,
      avgResolutionTime,
      alertsProcessedToday,
      alertsProcessedWeek,
      incidentsCreatedToday,
      incidentsResolvedToday,
      automationRate,
      falsePositiveRate,
      threatDetectionRate,
      escalationRate,
      avgFeedbackRating,
      analystProductivity,
      manualReviewsToday,
      weeklyTrends,
      severityDistribution,
      sourceMetrics
    };
  }

  private static calculatePrecisionFromData(incidents: Incident[], alerts: RawAlert[]): number {
    // Precision = True Positives / (True Positives + False Positives)
    // For simplicity, assume high-severity resolved incidents are true positives
    const truePositives = incidents.filter(inc => 
      inc.severity === 'critical' || inc.severity === 'high'
    ).length;

    const falsePositives = incidents.filter(inc => 
      inc.severity === 'low' && inc.status === 'resolved'
    ).length;

    return truePositives + falsePositives > 0 ? truePositives / (truePositives + falsePositives) : 0.85;
  }

  private static calculateRecallFromData(incidents: Incident[], alerts: RawAlert[]): number {
    // Recall = True Positives / (True Positives + False Negatives)
    // For simplicity, estimate based on alert-to-incident conversion rate
    const criticalAlerts = alerts.filter(alert => alert.severity === 'critical').length;
    const criticalIncidents = incidents.filter(inc => inc.severity === 'critical').length;

    return criticalAlerts > 0 ? Math.min(criticalIncidents / criticalAlerts, 1.0) : 0.82;
  }

  private static calculateAccuracyFromData(incidents: Incident[], alerts: RawAlert[]): number {
    // Simplified accuracy calculation
    const resolvedCorrectly = incidents.filter(inc => 
      inc.status === 'resolved' && (inc.severity === 'critical' || inc.severity === 'high')
    ).length;

    const totalIncidents = incidents.length;
    return totalIncidents > 0 ? resolvedCorrectly / totalIncidents : 0.88;
  }

  private static calculateAvgDetectionTime(alerts: RawAlert[], incidents: Incident[]): number {
    // Calculate average time from first related alert to incident creation
    let totalDetectionTime = 0;
    let validIncidents = 0;

    incidents.forEach(incident => {
      // Find alerts that could be related to this incident (simplified)
      const relatedAlerts = alerts.filter(alert => 
        Math.abs(new Date(alert.receivedAt).getTime() - new Date(incident.createdAt).getTime()) < 60 * 60 * 1000 // Within 1 hour
      );

      if (relatedAlerts.length > 0) {
        const earliestAlert = relatedAlerts.reduce((earliest, alert) => 
          new Date(alert.receivedAt) < new Date(earliest.receivedAt) ? alert : earliest
        );

        const detectionTime = new Date(incident.createdAt).getTime() - new Date(earliestAlert.receivedAt).getTime();
        totalDetectionTime += detectionTime;
        validIncidents++;
      }
    });

    return validIncidents > 0 ? totalDetectionTime / validIncidents / (1000 * 60) : 8.5; // minutes
  }

  private static calculateFalsePositiveRate(incidents: Incident[], feedback: Feedback[]): number {
    const negativeFeedback = feedback.filter(f => f.rating && f.rating <= 2).length;
    const totalFeedback = feedback.length;
    return totalFeedback > 0 ? negativeFeedback / totalFeedback : 0.05;
  }

  private static calculateThreatDetectionRate(alerts: RawAlert[], incidents: Incident[]): number {
    const highSeverityAlerts = alerts.filter(alert => 
      alert.severity === 'critical' || alert.severity === 'high'
    ).length;

    const threatIncidents = incidents.filter(inc => 
      inc.severity === 'critical' || inc.severity === 'high'
    ).length;

    return highSeverityAlerts > 0 ? threatIncidents / highSeverityAlerts : 0.75;
  }

  private static calculateEscalationRate(incidents: Incident[], actions: Action[]): number {
    const escalationActions = actions.filter(action => 
      action.actionType === 'ESCALATE' || action.actionType === 'ANALYST_ASSIGNMENT'
    ).length;

    return incidents.length > 0 ? escalationActions / incidents.length : 0.25;
  }

  private static calculateAnalystProductivity(manualActions: Action[], incidents: Incident[]): number {
    // Productivity = Actions per incident (higher is more productive)
    return incidents.length > 0 ? manualActions.length / incidents.length : 2.3;
  }

  private static calculateWeeklyTrends(alerts: RawAlert[], incidents: Incident[]): {
    alerts: number[];
    incidents: number[];
    responseTime: number[];
  } {
    const trends = {
      alerts: [] as number[],
      incidents: [] as number[],
      responseTime: [] as number[]
    };

    // Calculate daily metrics for the last 7 days
    for (let i = 6; i >= 0; i--) {
      const dayStart = new Date(Date.now() - i * 24 * 60 * 60 * 1000);
      dayStart.setHours(0, 0, 0, 0);
      const dayEnd = new Date(dayStart);
      dayEnd.setHours(23, 59, 59, 999);

      const dayAlerts = alerts.filter(alert => {
        const alertDate = new Date(alert.receivedAt);
        return alertDate >= dayStart && alertDate <= dayEnd;
      }).length;

      const dayIncidents = incidents.filter(inc => {
        const incDate = new Date(inc.createdAt);
        return incDate >= dayStart && incDate <= dayEnd;
      });

      const dayResponseTime = dayIncidents.length > 0
        ? dayIncidents.reduce((sum, inc) => {
            const responseTime = inc.closedAt 
              ? new Date(inc.closedAt).getTime() - new Date(inc.createdAt).getTime()
              : Date.now() - new Date(inc.createdAt).getTime();
            return sum + responseTime;
          }, 0) / dayIncidents.length / (1000 * 60) // minutes
        : 0;

      trends.alerts.push(dayAlerts);
      trends.incidents.push(dayIncidents.length);
      trends.responseTime.push(Math.round(dayResponseTime * 10) / 10);
    }

    return trends;
  }

  private static calculateSeverityDistribution(incidents: Incident[]): {
    critical: number;
    high: number;
    medium: number;
    low: number;
  } {
    const distribution = { critical: 0, high: 0, medium: 0, low: 0 };

    incidents.forEach(incident => {
      distribution[incident.severity]++;
    });

    return distribution;
  }

  private static async calculateSourceMetrics(sources: any[], alerts: RawAlert[], incidents: Incident[]): Promise<{
    sourceId: string;
    sourceName: string;
    alertCount: number;
    accuracy: number;
    falsePositiveRate: number;
  }[]> {
    return sources.map(source => {
      const sourceAlerts = alerts.filter(alert => alert.sourceId === source.id);

      // Simplified accuracy calculation per source
      const accuracy = sourceAlerts.length > 0 
        ? Math.random() * 0.2 + 0.8 // 80-100% for demo
        : 0;

      const falsePositiveRate = Math.random() * 0.1; // 0-10% for demo

      return {
        sourceId: source.id,
        sourceName: source.name,
        alertCount: sourceAlerts.length,
        accuracy,
        falsePositiveRate
      };
    });
  }
}

