import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { kafkaService } from "./kafka";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Custom API logger middleware
app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }
      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }
      log(logLine);
    }
  });

  next();
});

(async () => {
  // Initialize Kafka service for enterprise security event streaming (non-blocking)
  log('üéØ Initializing Kafka service for SOC operations...');
  // Don't await Kafka initialization to prevent blocking app startup
  kafkaService.initialize().catch(error => {
    log('‚ö†Ô∏è Kafka initialization failed, continuing without Kafka:', error.message);
  });

  const server = await registerRoutes(app);

  // Error handler middleware
  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";
    res.status(status).json({ message });
    throw err;
  });

  // Setup Vite in development mode only
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Use env.PORT or fallback to 5000
  const port = parseInt(process.env.PORT || "5000");

  // Graceful startup with EADDRINUSE check
  server.listen(
    {
      port,
      host: "0.0.0.0",
      // reusePort: true,
    },
    () => {
      log(`‚úÖ Server is running at http://localhost:${port}`);
    }
  ).on("error", (err: any) => {
    if (err.code === "EADDRINUSE") {
      console.error(`‚ùå Port ${port} is already in use. Try killing the process or use a different port.`);
      process.exit(1);
    } else {
      console.error(`‚ùå Unexpected server error: ${err.message}`);
      process.exit(1);
    }
  });
})();

