@import url('https://fonts.googleapis.com/css2?family=Inter:wght@100;200;300;400;500;600;700;800;900&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

/* ================================
   Enhanced Variables & Base Styles
   ================================ */
:root {
  --primary-dark: hsl(220, 15%, 5%);
  --primary-accent: hsl(330, 100%, 50%);
  --secondary-accent: hsl(267, 100%, 67%);
  --text-primary: hsl(0, 0%, 98%);
  --text-secondary: hsl(0, 0%, 70%);
  --border-subtle: hsla(330, 100%, 50%, 0.2);
  --glow-color: hsla(330, 100%, 50%, 0.6);
  --secondary-glow: hsla(267, 100%, 67%, 0.4);
}

* {
  box-sizing: border-box;
}

html {
  font-family: 'Inter', system-ui, -apple-system, sans-serif;
  line-height: 1.6;
  font-weight: 400;
  scroll-behavior: smooth;
}

body {
  font-family: 'Inter', system-ui, -apple-system, sans-serif;
  background: var(--primary-dark);
  color: var(--text-primary);
  line-height: 1.6;
  overflow-x: hidden;
}

/* ================================
   Enhanced Glow Effects & Animations
   ================================ */
.glow-text {
  text-shadow:
    0 0 5px var(--glow-color),
    0 0 10px var(--glow-color),
    0 0 15px var(--glow-color),
    0 0 20px var(--glow-color);
  animation: text-glow 2s ease-in-out infinite alternate;
}

@keyframes text-glow {
  from {
    text-shadow:
      0 0 5px var(--glow-color),
      0 0 10px var(--glow-color),
      0 0 15px var(--glow-color),
      0 0 20px var(--glow-color);
  }
  to {
    text-shadow:
      0 0 10px var(--glow-color),
      0 0 20px var(--glow-color),
      0 0 30px var(--glow-color),
      0 0 40px var(--glow-color);
  }
}

.gradient-glow {
  background: radial-gradient(
    circle at center,
    hsla(330, 100%, 50%, 0.15) 0%,
    hsla(267, 100%, 67%, 0.1) 25%,
    transparent 50%
  );
  animation: gradient-shift 4s ease-in-out infinite;
}

@keyframes gradient-shift {
  0%, 100% { transform: scale(1) rotate(0deg); opacity: 0.8; }
  50% { transform: scale(1.1) rotate(180deg); opacity: 1; }
}

/* ================================
   Enhanced Button Styles
   ================================ */
.glow-button {
  position: relative;
  overflow: hidden;
  box-shadow:
    0 0 20px var(--glow-color),
    inset 0 0 20px hsla(330, 100%, 50%, 0.1);
  transition: all 0.3s ease;
}

.glow-button::before {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: linear-gradient(
    45deg,
    transparent,
    hsla(330, 100%, 50%, 0.2),
    transparent
  );
  transform: rotate(45deg);
  transition: all 0.6s;
  opacity: 0;
}

.glow-button:hover::before {
  animation: shimmer 0.6s ease-in-out;
}

@keyframes shimmer {
  0% { transform: translateX(-100%) translateY(-100%) rotate(45deg); opacity: 0; }
  50% { opacity: 1; }
  100% { transform: translateX(100%) translateY(100%) rotate(45deg); opacity: 0; }
}

/* ================================
   Enhanced SOC Dashboard Animations
   ================================ */
.cortex-glow { animation: cortex-glow 3s ease-in-out infinite; }
.ai-core-glow { animation: ai-core-glow 4s ease-in-out infinite; }
.alert-pulse { animation: alert-pulse 2s ease-in-out infinite; }
.incident-pulse { animation: incident-pulse 2.5s ease-in-out infinite; }
.sidebar-glow { animation: sidebar-glow 5s ease-in-out infinite; }
.dashboard-glow { animation: dashboard-glow 6s ease-in-out infinite; }

@keyframes cortex-glow {
  0%, 100% {
    box-shadow: 0 0 20px var(--glow-color);
    border-color: var(--primary-accent);
  }
  50% {
    box-shadow: 0 0 40px var(--glow-color), 0 0 60px var(--secondary-glow);
    border-color: var(--secondary-accent);
  }
}

@keyframes ai-core-glow {
  0%, 100% {
    background: linear-gradient(135deg, var(--primary-accent) 0%, var(--secondary-accent) 100%);
    transform: scale(1);
  }
  50% {
    background: linear-gradient(135deg, var(--secondary-accent) 0%, var(--primary-accent) 100%);
    transform: scale(1.05);
  }
}

@keyframes alert-pulse {
  0%, 100% {
    background-color: hsl(0, 70%, 50%);
    box-shadow: 0 0 10px hsla(0, 70%, 50%, 0.5);
  }
  50% {
    background-color: hsl(0, 85%, 60%);
    box-shadow: 0 0 20px hsla(0, 85%, 60%, 0.8);
  }
}

/* ================================
   Writing Animation Effects
   ================================ */
.typewriter {
  overflow: hidden;
  border-right: 2px solid var(--primary-accent);
  white-space: nowrap;
  animation: typing 3s steps(40, end), blink-caret 0.75s step-end infinite;
}

@keyframes typing {
  from { width: 0; }
  to { width: 100%; }
}

@keyframes blink-caret {
  from, to { border-color: transparent; }
  50% { border-color: var(--primary-accent); }
}

/* ================================
   Enhanced Interactive Elements
   ================================ */
.response-automated { animation: response-glow-pink 3s ease-in-out infinite; }
.response-manual { animation: response-glow-blue 3.5s ease-in-out infinite; }

@keyframes response-glow-pink {
  0%, 100% {
    box-shadow: 0 0 15px hsla(330, 100%, 50%, 0.3);
    border-color: hsla(330, 100%, 50%, 0.5);
  }
  50% {
    box-shadow: 0 0 30px hsla(330, 100%, 50%, 0.6);
    border-color: hsla(330, 100%, 50%, 0.8);
  }
}

@keyframes response-glow-blue {
  0%, 100% {
    box-shadow: 0 0 15px hsla(220, 100%, 60%, 0.3);
    border-color: hsla(220, 100%, 60%, 0.5);
  }
  50% {
    box-shadow: 0 0 30px hsla(220, 100%, 60%, 0.6);
    border-color: hsla(220, 100%, 60%, 0.8);
  }
}

/* Enhanced hover states */
.nav-item-active {
  box-shadow: 0 0 15px hsla(330, 100%, 50%, 0.25);
}

.nav-item-hover:hover {
  background: hsla(330, 100%, 50%, 0.08);
  box-shadow: 0 0 10px hsla(330, 100%, 50%, 0.2);
  transform: translateY(-2px);
  transition: all 0.3s ease;
}

.data-source-hover:hover {
  background: hsla(220, 15%, 8%, 0.8);
  border-color: hsla(330, 100%, 50%, 0.25);
  transform: translateX(5px);
  transition: all 0.3s ease;
}

.stats-card-hover:hover {
  border-color: hsla(330, 100%, 50%, 0.35);
  transform: translateY(-2px);
  box-shadow: 0 10px 25px hsla(330, 100%, 50%, 0.1);
  transition: all 0.3s ease;
}

/* ================================
   Advanced Data Visualization
   ================================ */
.data-flow-line {
  position: relative;
  overflow: hidden;
}

.data-flow-line::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent,
    var(--primary-accent),
    transparent
  );
  animation: data-flow 2s linear infinite;
}

@keyframes data-flow {
  0% { left: -100%; }
  100% { left: 100%; }
}

/* ================================
   Scrollbar Styling
   ================================ */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--primary-dark);
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, var(--primary-accent), var(--secondary-accent));
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(to bottom, var(--secondary-accent), var(--primary-accent));
}

/* ================================
   Enhanced Responsive Design
   ================================ */
@media (max-width: 768px) {
  .glow-text {
    font-size: clamp(2rem, 8vw, 4rem);
  }

  .gradient-glow {
    opacity: 0.7;
  }
}

/* ================================
   Utility Classes
   ================================ */
.animate-float {
  animation: float 3s ease-in-out infinite;
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px var(--glow-color);
    opacity: 0.8;
  }
  50% {
    box-shadow: 0 0 40px var(--glow-color), 0 0 60px var(--secondary-glow);
    opacity: 1;
  }
}

.glass-morphism {
  background: hsla(220, 15%, 10%, 0.8);
  backdrop-filter: blur(20px);
  border: 1px solid hsla(330, 100%, 50%, 0.2);
}

