import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { AlertTriangle, Shield, Clock, Plus } from "lucide-react";
import { cn } from "@/lib/utils";

interface Alert {
  id: string;
  title: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
  timestamp: Date;
  source?: string;
  description?: string;
  payload?: Record<string, any>;
}

interface AlertStreamProps {
  alerts: Alert[];
  onCreateIncident: (alert: Alert) => void;
  isCreatingIncident?: boolean;
  className?: string;
}

export function AlertStream({ 
  alerts, 
  onCreateIncident, 
  isCreatingIncident = false,
  className 
}: AlertStreamProps) {
  const getSeverityConfig = (severity: Alert['severity']) => {
    switch (severity) {
      case 'critical':
        return {
          color: 'border-red-500',
          bgColor: 'bg-red-500/10',
          iconColor: 'text-red-500',
          badgeClass: 'soc-status-critical'
        };
      case 'high':
        return {
          color: 'border-amber-500',
          bgColor: 'bg-amber-500/10',
          iconColor: 'text-amber-500',
          badgeClass: 'soc-status-high'
        };
      case 'medium':
        return {
          color: 'border-sky-500',
          bgColor: 'bg-sky-500/10',
          iconColor: 'text-sky-500',
          badgeClass: 'soc-status-medium'
        };
      case 'low':
        return {
          color: 'border-emerald-500',
          bgColor: 'bg-emerald-500/10',
          iconColor: 'text-emerald-500',
          badgeClass: 'soc-status-low'
        };
      default:
        return {
          color: 'border-slate-500',
          bgColor: 'bg-slate-500/10',
          iconColor: 'text-slate-500',
          badgeClass: 'soc-status-medium'
        };
    }
  };

  const getTimeAgo = (timestamp: Date) => {
    const now = new Date();
    const diffMs = now.getTime() - timestamp.getTime();
    const diffSeconds = Math.floor(diffMs / 1000);
    const diffMinutes = Math.floor(diffSeconds / 60);
    const diffHours = Math.floor(diffMinutes / 60);

    if (diffSeconds < 60) {
      return `${diffSeconds}s ago`;
    } else if (diffMinutes < 60) {
      return `${diffMinutes}m ago`;
    } else if (diffHours < 24) {
      return `${diffHours}h ago`;
    } else {
      return timestamp.toLocaleDateString();
    }
  };

  return (
    <div className={cn("space-y-3 max-h-96 overflow-y-auto", className)} data-testid="alert-stream">
      {alerts.map((alert, index) => {
        const severityConfig = getSeverityConfig(alert.severity);

        return (
          <div
            key={`${alert.id}-${index}`}
            className={cn(
              "flex items-center justify-between p-4 bg-slate-750 rounded-lg border-l-4 transition-all duration-200 hover:bg-slate-700",
              severityConfig.color
            )}
            data-testid={`alert-${index}`}
          >
            <div className="flex items-center space-x-4 flex-1 min-w-0">
              <div className={cn("w-8 h-8 rounded-lg flex items-center justify-center flex-shrink-0", severityConfig.bgColor)}>
                {alert.severity === 'critical' || alert.severity === 'high' ? (
                  <AlertTriangle className={cn("w-4 h-4", severityConfig.iconColor)} />
                ) : (
                  <Shield className={cn("w-4 h-4", severityConfig.iconColor)} />
                )}
              </div>
              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-2 mb-1">
                  <p className="text-sm font-medium text-white truncate" data-testid={`alert-${index}-title`}>
                    {alert.title}
                  </p>
                  <Badge className={severityConfig.badgeClass} data-testid={`alert-${index}-severity`}>
                    {alert.severity}
                  </Badge>
                </div>
                <p className="text-xs text-slate-400 truncate" data-testid={`alert-${index}-source`}>
                  {alert.source ? `Source: ${alert.source}` : 'Unknown source'}
                  {alert.payload && Object.keys(alert.payload).length > 0 && (
                    <span className="ml-2">
                      ‚Ä¢ {Object.entries(alert.payload).slice(0, 2).map(([key, value]) => 
                        `${key}: ${value}`
                      ).join(' ‚Ä¢ ')}
                    </span>
                  )}
                </p>
              </div>
            </div>

            <div className="flex items-center space-x-3 flex-shrink-0">
              <div className="flex items-center space-x-1 text-xs text-slate-400">
                <Clock className="w-3 h-3" />
                <span data-testid={`alert-${index}-time`}>
                  {getTimeAgo(alert.timestamp)}
                </span>
              </div>
              <Button
                variant="ghost"
                size="sm"
                className="text-sky-400 hover:text-sky-300 hover:bg-sky-500/10"
                onClick={() => onCreateIncident(alert)}
                disabled={isCreatingIncident}
                data-testid={`create-incident-${index}`}
              >
                <Plus className="w-4 h-4 mr-1" />
                {isCreatingIncident ? "Creating..." : "Create Incident"}
              </Button>
            </div>
          </div>
        );
      })}
    </div>
  );
}

client/src/components/export-dialog.tsx.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Download, FileText, Database } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";

interface ExportDialogProps {
  type: 'incidents' | 'alerts' | 'analytics' | 'actions';
  triggerText?: string;
  className?: string;
}

interface ExportFilters {
  startDate?: string;
  endDate?: string;
  severity?: string[];
  status?: string[];
  sourceId?: string;
}

export function ExportDialog({ type, triggerText, className }: ExportDialogProps) {
  const [open, setOpen] = useState(false);
  const [format, setFormat] = useState<'csv' | 'json'>('csv');
  const [isExporting, setIsExporting] = useState(false);
  const [filters, setFilters] = useState<ExportFilters>({});
  const { toast } = useToast();

  const handleExport = async () => {
    try {
      setIsExporting(true);

      let response: Response;

      switch (type) {
        case 'incidents':
          response = await api.exportIncidents(format, filters);
          break;
        case 'alerts':
          response = await api.exportAlerts(format, filters);
          break;
        case 'analytics':
          response = await api.exportAnalytics(format, {
            startDate: filters.startDate,
            endDate: filters.endDate,
          });
          break;
        case 'actions':
          response = await api.exportActions(format, {
            startDate: filters.startDate,
            endDate: filters.endDate,
          });
          break;
        default:
          throw new Error('Unknown export type');
      }

      if (!response.ok) {
        throw new Error('Export failed');
      }

      // Get the filename from Content-Disposition header or generate one
      const contentDisposition = response.headers.get('Content-Disposition');
      const filenameMatch = contentDisposition?.match(/filename="(.+)"/);
      const filename = filenameMatch ? filenameMatch[1] : `soc-${type}-export.${format}`;

      // Create blob and download
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      toast({
        title: "Export Complete",
        description: `${type} data exported successfully as ${format.toUpperCase()}`,
      });

      setOpen(false);
    } catch (error) {
      console.error('Export error:', error);
      toast({
        title: "Export Failed",
        description: `Failed to export ${type} data. Please try again.`,
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  };

  const severityOptions = ['critical', 'high', 'medium', 'low'];
  const statusOptions = ['open', 'investigating', 'monitoring', 'resolved'];

  const handleSeverityToggle = (severity: string, checked: boolean) => {
    setFilters(prev => ({
      ...prev,
      severity: checked 
        ? [...(prev.severity || []), severity]
        : (prev.severity || []).filter(s => s !== severity)
    }));
  };

  const handleStatusToggle = (status: string, checked: boolean) => {
    setFilters(prev => ({
      ...prev,
      status: checked 
        ? [...(prev.status || []), status]
        : (prev.status || []).filter(s => s !== status)
    }));
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button className={className || "soc-button-primary"} data-testid={`export-${type}`}>
          <Download className="w-4 h-4 mr-2" />
          {triggerText || `Export ${type.charAt(0).toUpperCase() + type.slice(1)}`}
        </Button>
      </DialogTrigger>
      <DialogContent className="bg-slate-800 border-slate-700 max-w-md">
        <DialogHeader>
          <DialogTitle className="text-white">
            Export {type.charAt(0).toUpperCase() + type.slice(1)} Data
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Format Selection */}
          <div className="space-y-2">
            <Label className="text-slate-300">Export Format</Label>
            <Select value={format} onValueChange={(value: 'csv' | 'json') => setFormat(value)}>
              <SelectTrigger className="bg-slate-700 border-slate-600 text-white">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="csv">
                  <div className="flex items-center">
                    <FileText className="w-4 h-4 mr-2" />
                    CSV (Excel Compatible)
                  </div>
                </SelectItem>
                <SelectItem value="json">
                  <div className="flex items-center">
                    <Database className="w-4 h-4 mr-2" />
                    JSON (Structured Data)
                  </div>
                </SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Date Range */}
          <div className="space-y-2">
            <Label className="text-slate-300">Date Range (Optional)</Label>
            <div className="grid grid-cols-2 gap-2">
              <div>
                <Label className="text-xs text-slate-400">Start Date</Label>
                <Input
                  type="date"
                  value={filters.startDate || ''}
                  onChange={(e) => setFilters(prev => ({ ...prev, startDate: e.target.value }))}
                  className="bg-slate-700 border-slate-600 text-white"
                />
              </div>
              <div>
                <Label className="text-xs text-slate-400">End Date</Label>
                <Input
                  type="date"
                  value={filters.endDate || ''}
                  onChange={(e) => setFilters(prev => ({ ...prev, endDate: e.target.value }))}
                  className="bg-slate-700 border-slate-600 text-white"
                />
              </div>
            </div>
          </div>

          {/* Filters specific to incidents and alerts */}
          {(type === 'incidents' || type === 'alerts') && (
            <div className="space-y-4">
              {/* Severity Filter */}
              <div className="space-y-2">
                <Label className="text-slate-300">Severity (Optional)</Label>
                <div className="grid grid-cols-2 gap-2">
                  {severityOptions.map((severity) => (
                    <div key={severity} className="flex items-center space-x-2">
                      <Checkbox
                        id={`severity-${severity}`}
                        checked={filters.severity?.includes(severity) || false}
                        onCheckedChange={(checked) => handleSeverityToggle(severity, checked as boolean)}
                      />
                      <Label 
                        htmlFor={`severity-${severity}`} 
                        className="text-sm text-slate-300 capitalize"
                      >
                        {severity}
                      </Label>
                    </div>
                  ))}
                </div>
              </div>

              {/* Status Filter (incidents only) */}
              {type === 'incidents' && (
                <div className="space-y-2">
                  <Label className="text-slate-300">Status (Optional)</Label>
                  <div className="grid grid-cols-2 gap-2">
                    {statusOptions.map((status) => (
                      <div key={status} className="flex items-center space-x-2">
                        <Checkbox
                          id={`status-${status}`}
                          checked={filters.status?.includes(status) || false}
                          onCheckedChange={(checked) => handleStatusToggle(status, checked as boolean)}
                        />
                        <Label 
                          htmlFor={`status-${status}`} 
                          className="text-sm text-slate-300 capitalize"
                        >
                          {status}
                        </Label>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          {/* Export Actions */}
          <div className="flex justify-end space-x-2 pt-4">
            <Button
              variant="ghost"
              onClick={() => setOpen(false)}
              disabled={isExporting}
            >
              Cancel
            </Button>
            <Button
              onClick={handleExport}
              disabled={isExporting}
              className="soc-button-primary"
              data-testid="confirm-export"
            >
              {isExporting ? "Exporting..." : "Export Data"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/incident-card.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { AlertTriangle, Eye, UserPlus, TrendingUp, Clock, User } from "lucide-react";
import { cn } from "@/lib/utils";
import { Incident } from "@shared/schema";

interface IncidentCardProps {
  incident: Incident;
  onView?: () => void;
  onAssign?: () => void;
  onEscalate?: () => void;
  isLoading?: boolean;
  className?: string;
  testId?: string;
}

export function IncidentCard({ 
  incident,
  onView,
  onAssign,
  onEscalate,
  isLoading = false,
  className,
  testId = "incident-card"
}: IncidentCardProps) {
  const getSeverityInfo = (confidence: number) => {
    if (confidence > 0.8) return { label: 'Critical', color: 'critical', dotColor: 'bg-red-500' };
    if (confidence > 0.5) return { label: 'High', color: 'high', dotColor: 'bg-amber-500' };
    if (confidence > 0.3) return { label: 'Medium', color: 'medium', dotColor: 'bg-sky-500' };
    return { label: 'Low', color: 'low', dotColor: 'bg-emerald-500' };
  };

  const getTimeAgo = (date: string | Date) => {
    const now = new Date();
    const incidentDate = new Date(date);
    const diffMs = now.getTime() - incidentDate.getTime();
    const diffMinutes = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffMinutes < 60) {
      return `${diffMinutes}m ago`;
    } else if (diffHours < 24) {
      return `${diffHours}h ago`;
    } else if (diffDays < 7) {
      return `${diffDays}d ago`;
    } else {
      return incidentDate.toLocaleDateString();
    }
  };

  const severity = getSeverityInfo(incident.confidence);
  const isOpen = incident.status === 'open';

  return (
    <Card className={cn("soc-card transition-all duration-200 hover:border-slate-600", className)} data-testid={testId}>
      <CardContent className="p-6">
        <div className="flex items-start space-x-3 mb-4">
          <div className={cn("w-3 h-3 rounded-full mt-2 flex-shrink-0", severity.dotColor)}></div>
          <div className="flex-1 min-w-0">
            <div className="flex items-start justify-between mb-2">
              <h3 className="text-sm font-medium text-white truncate" data-testid={`${testId}-title`}>
                Security Incident #{incident.id.slice(-6)}
              </h3>
              <Badge 
                className={`soc-status-${severity.color} ml-2 flex-shrink-0`}
                data-testid={`${testId}-severity`}
              >
                {severity.label}
              </Badge>
            </div>

            <p className="text-xs text-slate-400 mb-3" data-testid={`${testId}-description`}>
              {incident.decision === 'AUTO' ? 'Automated detection' : 'Manual review required'} 
              {' ‚Ä¢ '}
              {(incident.confidence * 100).toFixed(1)}% confidence
            </p>

            <div className="flex items-center space-x-4 text-xs text-slate-400 mb-4">
              <div className="flex items-center space-x-1">
                <Clock className="w-3 h-3" />
                <span data-testid={`${testId}-time`}>
                  {getTimeAgo(incident.createdAt)}
                </span>
              </div>
              <div className="flex items-center space-x-1">
                <User className="w-3 h-3" />
                <span data-testid={`${testId}-status`}>
                  {incident.status}
                </span>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              {onView && (
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-sky-400 hover:text-sky-300 hover:bg-sky-500/10 h-8 px-2"
                  onClick={onView}
                  disabled={isLoading}
                  data-testid={`${testId}-view`}
                >
                  <Eye className="w-3 h-3 mr-1" />
                  View
                </Button>
              )}

              {onAssign && isOpen && (
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-emerald-400 hover:text-emerald-300 hover:bg-emerald-500/10 h-8 px-2"
                  onClick={onAssign}
                  disabled={isLoading}
                  data-testid={`${testId}-assign`}
                >
                  <UserPlus className="w-3 h-3 mr-1" />
                  Assign
                </Button>
              )}

              {onEscalate && isOpen && (
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-red-400 hover:text-red-300 hover:bg-red-500/10 h-8 px-2"
                  onClick={onEscalate}
                  disabled={isLoading}
                  data-testid={`${testId}-escalate`}
                >
                  <TrendingUp className="w-3 h-3 mr-1" />
                  Escalate
                </Button>
              )}
            </div>
          </div>
        </div>

        {/* Additional incident metadata */}
        <div className="pt-3 border-t border-slate-700">
          <div className="flex items-center justify-between text-xs">
            <span className="text-slate-500">ID: {incident.id}</span>
            <span className="text-slate-500">
              Decision: {incident.decision}
            </span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/metrics-chart.tsx.content:
import { ResponsiveContainer, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, BarChart, Bar, PieChart, Pie, Cell } from 'recharts';
import { cn } from "@/lib/utils";

interface ChartData {
  name: string;
  value: number | string;
  category?: string;
}

interface MetricsChartProps {
  data: ChartData[];
  type?: 'line' | 'bar' | 'pie';
  height?: number;
  className?: string;
  color?: string;
  showGrid?: boolean;
  showTooltip?: boolean;
}

const COLORS = [
  'hsl(207, 90%, 54%)', // sky-500
  'hsl(160, 84%, 39%)', // emerald-500
  'hsl(43, 96%, 56%)',  // amber-500
  'hsl(0, 84%, 60%)',   // red-500
  'hsl(262, 83%, 58%)', // purple-500
  'hsl(142, 71%, 45%)', // green-500
];

export function MetricsChart({ 
  data, 
  type = 'line',
  height = 300,
  className,
  color = 'hsl(207, 90%, 54%)',
  showGrid = true,
  showTooltip = true
}: MetricsChartProps) {
  const numericData = data.map(item => ({
    ...item,
    value: typeof item.value === 'string' ? parseFloat(item.value) || 0 : item.value
  }));

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-slate-800 border border-slate-600 rounded-lg p-3 shadow-lg">
          <p className="text-slate-300 text-sm font-medium">{label}</p>
          {payload.map((entry: any, index: number) => (
            <p key={index} className="text-white text-sm">
              <span className="font-medium">{entry.name}:</span> {entry.value}
              {type === 'line' && entry.name === 'value' && 'ms'}
              {type === 'bar' && '%'}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  if (numericData.length === 0) {
    return (
      <div 
        className={cn("flex items-center justify-center text-slate-400 bg-slate-750 rounded-lg border border-slate-600", className)}
        style={{ height }}
        data-testid="metrics-chart-empty"
      >
        <div className="text-center">
          <div className="w-12 h-12 bg-slate-600 rounded-lg flex items-center justify-center mx-auto mb-2">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"></path>
            </svg>
          </div>
          <p className="text-sm">No data available</p>
        </div>
      </div>
    );
  }

  const renderChart = () => {
    switch (type) {
      case 'bar':
        return (
          <BarChart data={numericData}>
            {showGrid && <CartesianGrid strokeDasharray="3 3" stroke="hsl(215, 25%, 27%)" />}
            <XAxis 
              dataKey="name" 
              stroke="hsl(215, 20%, 65%)" 
              fontSize={12}
              tickLine={false}
              axisLine={false}
            />
            <YAxis 
              stroke="hsl(215, 20%, 65%)" 
              fontSize={12}
              tickLine={false}
              axisLine={false}
            />
            {showTooltip && <Tooltip content={<CustomTooltip />} />}
            <Bar 
              dataKey="value" 
              fill={color}
              radius={[4, 4, 0, 0]}
            />
          </BarChart>
        );

      case 'pie':
        return (
          <PieChart>
            <Pie
              data={numericData}
              cx="50%"
              cy="50%"
              outerRadius={height / 3}
              fill={color}
              dataKey="value"
              label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
            >
              {numericData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            {showTooltip && <Tooltip content={<CustomTooltip />} />}
          </PieChart>
        );

      case 'line':
      default:
        return (
          <LineChart data={numericData}>
            {showGrid && <CartesianGrid strokeDasharray="3 3" stroke="hsl(215, 25%, 27%)" />}
            <XAxis 
              dataKey="name" 
              stroke="hsl(215, 20%, 65%)" 
              fontSize={12}
              tickLine={false}
              axisLine={false}
            />
            <YAxis 
              stroke="hsl(215, 20%, 65%)" 
              fontSize={12}
              tickLine={false}
              axisLine={false}
            />
            {showTooltip && <Tooltip content={<CustomTooltip />} />}
            <Line 
              type="monotone" 
              dataKey="value" 
              stroke={color}
              strokeWidth={2}
              dot={{ fill: color, strokeWidth: 2, r: 4 }}
              activeDot={{ r: 6, stroke: color, strokeWidth: 2, fill: 'hsl(222, 84%, 4.9%)' }}
            />
          </LineChart>
        );
    }
  };

  return (
    <div className={cn("w-full", className)} data-testid="metrics-chart">
      <ResponsiveContainer width="100%" height={height}>
        {renderChart()}
      </ResponsiveContainer>
    </div>
  );
}

client/src/components/source-card.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Shield, Server, Activity, Trash2, Edit, AlertCircle } from "lucide-react";
import { cn } from "@/lib/utils";
import { Source } from "@shared/schema";

interface SourceCardProps {
  source: Source;
  onToggle: (enabled: boolean) => void;
  onDelete: () => void;
  onEdit?: () => void;
  isDeleting?: boolean;
  isToggling?: boolean;
  testId?: string;
  className?: string;
}

export function SourceCard({ 
  source, 
  onToggle, 
  onDelete, 
  onEdit,
  isDeleting = false,
  isToggling = false,
  testId = "source-card",
  className 
}: SourceCardProps) {
  const getSourceIcon = (type: string) => {
    switch (type.toLowerCase()) {
      case 'siem':
        return Shield;
      case 'edr':
        return Activity;
      case 'firewall':
        return Server;
      default:
        return Server;
    }
  };

  const getSourceIconColor = (type: string) => {
    switch (type.toLowerCase()) {
      case 'siem':
        return 'text-sky-500';
      case 'edr':
        return 'text-emerald-500';
      case 'firewall':
        return 'text-amber-500';
      default:
        return 'text-slate-500';
    }
  };

  const getSourceIconBg = (type: string) => {
    switch (type.toLowerCase()) {
      case 'siem':
        return 'bg-sky-500/10';
      case 'edr':
        return 'bg-emerald-500/10';
      case 'firewall':
        return 'bg-amber-500/10';
      default:
        return 'bg-slate-500/10';
    }
  };

  const isEnabled = (source.config as any)?.enabled !== false;
  const IconComponent = getSourceIcon(source.type);

  // Mock some realistic data for demonstration
  const lastEventTime = new Date(Date.now() - Math.random() * 3600000).toISOString();
  const eventsPerHour = Math.floor(Math.random() * 5000) + 100;

  const getLastEventDisplay = () => {
    const now = new Date();
    const lastEvent = new Date(lastEventTime);
    const diffMs = now.getTime() - lastEvent.getTime();
    const diffMinutes = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));

    if (diffMinutes < 60) {
      return `${diffMinutes}m ago`;
    } else if (diffHours < 24) {
      return `${diffHours}h ago`;
    } else {
      return lastEvent.toLocaleDateString();
    }
  };

  return (
    <Card className={cn("soc-card transition-all duration-200 hover:border-slate-600", className)} data-testid={testId}>
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <div className={cn("w-10 h-10 rounded-lg flex items-center justify-center", getSourceIconBg(source.type))}>
            <IconComponent className={cn("w-5 h-5", getSourceIconColor(source.type))} />
          </div>
          <div className="flex items-center space-x-2">
            <Badge 
              className={isEnabled ? "soc-status-low" : "soc-status-critical"}
              data-testid={`${testId}-status`}
            >
              {isEnabled ? "Active" : "Inactive"}
            </Badge>
            {!isEnabled && (
              <AlertCircle className="w-4 h-4 text-amber-500" />
            )}
          </div>
        </div>

        <div className="mb-4">
          <h3 className="text-lg font-medium text-white mb-2" data-testid={`${testId}-name`}>
            {source.name}
          </h3>
          <p className="text-sm text-slate-400 mb-3" data-testid={`${testId}-type`}>
            {source.type} ‚Ä¢ {(source.config as any)?.endpoint ? 'Configured' : 'Configuration needed'}
          </p>
        </div>

        <div className="space-y-2 mb-4">
          <div className="flex items-center justify-between text-sm">
            <span className="text-slate-400">Last Event:</span>
            <span className="text-white" data-testid={`${testId}-last-event`}>
              {isEnabled ? getLastEventDisplay() : 'N/A'}
            </span>
          </div>
          <div className="flex items-center justify-between text-sm">
            <span className="text-slate-400">Events/hr:</span>
            <span className="text-white" data-testid={`${testId}-events-per-hour`}>
              {isEnabled ? eventsPerHour.toLocaleString() : '0'}
            </span>
          </div>
          {(source.config as any)?.endpoint && (
            <div className="flex items-center justify-between text-sm">
              <span className="text-slate-400">Endpoint:</span>
              <span className="text-slate-300 text-xs truncate max-w-32" title={(source.config as any).endpoint}>
                {(source.config as any).endpoint}
              </span>
            </div>
          )}
        </div>

        <div className="flex items-center justify-between pt-4 border-t border-slate-700">
          <div className="flex items-center space-x-2">
            <Switch
              checked={isEnabled}
              onCheckedChange={onToggle}
              disabled={isToggling}
              data-testid={`${testId}-toggle`}
            />
            <span className="text-sm text-slate-400">
              {isToggling ? "Updating..." : isEnabled ? "Enabled" : "Disabled"}
            </span>
          </div>

          <div className="flex items-center space-x-1">
            {onEdit && (
              <Button
                variant="ghost"
                size="sm"
                className="text-slate-400 hover:text-white hover:bg-slate-700"
                onClick={onEdit}
                data-testid={`${testId}-edit`}
              >
                <Edit className="w-4 h-4" />
              </Button>
            )}
            <Button
              variant="ghost"
              size="sm"
              className="text-red-400 hover:text-red-300 hover:bg-red-500/10"
              onClick={onDelete}
              disabled={isDeleting}
              data-testid={`${testId}-delete`}
            >
              <Trash2 className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-realtime-updates.ts.content:
import { useEffect } from 'react';
import { useWebSocket } from './use-websocket';
import { queryClient } from '@/lib/queryClient';
import { useToast } from './use-toast';

interface WebSocketEvent {
  type: string;
  data?: any;
  message?: string;
}

export function useRealtimeUpdates() {
  const { lastMessage, connectionStatus } = useWebSocket();
  const { toast } = useToast();

  useEffect(() => {
    if (!lastMessage) return;

    const event = lastMessage as WebSocketEvent;

    switch (event.type) {
      case 'incident_created':
        queryClient.invalidateQueries({ queryKey: ['/api/incidents'] });
        queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
        toast({
          title: "New Incident",
          description: `Incident ${event.data?.title || 'Unknown'} has been created`,
        });
        break;

      case 'incident_updated':
        queryClient.invalidateQueries({ queryKey: ['/api/incidents'] });
        queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
        if (event.data?.id) {
          queryClient.invalidateQueries({ queryKey: ['/api/incidents', event.data.id] });
        }
        toast({
          title: "Incident Updated",
          description: `Incident ${event.data?.title || 'Unknown'} has been updated`,
        });
        break;

      case 'alert_created':
        queryClient.invalidateQueries({ queryKey: ['/api/alerts'] });
        queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
        toast({
          title: "New Alert",
          description: `New ${event.data?.severity || 'unknown'} severity alert received`,
        });
        break;

      case 'alert_normalized':
        queryClient.invalidateQueries({ queryKey: ['/api/alerts'] });
        queryClient.invalidateQueries({ queryKey: ['/api/incidents'] });
        queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
        break;

      case 'alerts_simulated':
        queryClient.invalidateQueries({ queryKey: ['/api/alerts'] });
        queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
        toast({
          title: "Alerts Simulated",
          description: `${event.data?.count || 0} ${event.data?.sourceType || 'unknown'} alerts simulated`,
        });
        break;

      case 'source_created':
      case 'source_updated':
      case 'source_deleted':
        queryClient.invalidateQueries({ queryKey: ['/api/sources'] });
        break;

      case 'action_created':
        queryClient.invalidateQueries({ queryKey: ['/api/actions'] });
        if (event.data?.incidentId) {
          queryClient.invalidateQueries({ queryKey: ['/api/incidents', event.data.incidentId, 'actions'] });
        }
        break;

      case 'feedback_created':
        queryClient.invalidateQueries({ queryKey: ['/api/feedback'] });
        if (event.data?.incidentId) {
          queryClient.invalidateQueries({ queryKey: ['/api/incidents', event.data.incidentId, 'feedback'] });
        }
        break;

      case 'metric_created':
        queryClient.invalidateQueries({ queryKey: ['/api/metrics'] });
        break;

      case 'realtime_simulation_started':
        toast({
          title: "Real-time Simulation Started",
          description: `${event.data?.sourceType} alerts will be generated for ${event.data?.durationMinutes} minutes`,
        });
        break;

      case 'connected':
        // Handle initial connection
        console.log('WebSocket connected successfully');
        break;

      default:
        console.log('Unknown WebSocket event:', event.type, event);
    }
  }, [lastMessage, toast]);

  return {
    connectionStatus,
    isConnected: connectionStatus === 'connected',
    isConnecting: connectionStatus === 'connecting',
    isDisconnected: connectionStatus === 'disconnected'
  };
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

