import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { MessageSquare, Star, Plus, User } from "lucide-react";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Skeleton } from "@/components/ui/skeleton";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { insertFeedbackSchema } from "@shared/schema";
import { useState } from "react";

const feedbackFormSchema = insertFeedbackSchema.extend({
  rating: z.number().min(1).max(5),
  comment: z.string().min(10, "Comment must be at least 10 characters"),
});

export default function Feedback() {
  const { toast } = useToast();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);

  const { data: feedback, isLoading: feedbackLoading } = useQuery({
    queryKey: ['/api/feedback'],
    queryFn: api.getFeedback,
  });

  const { data: incidents } = useQuery({
    queryKey: ['/api/incidents'],
    queryFn: api.getIncidents,
  });

  const createFeedbackMutation = useMutation({
    mutationFn: api.createFeedback,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/feedback'] });
      setIsCreateDialogOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Feedback submitted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit feedback",
        variant: "destructive",
      });
    },
  });

  const form = useForm<z.infer<typeof feedbackFormSchema>>({
    resolver: zodResolver(feedbackFormSchema),
    defaultValues: {
      incidentId: "",
      userId: "demo-user-id", // In real app, this would come from auth
      feedback: {},
      rating: 5,
      comment: "",
    },
  });

  const onSubmit = (values: z.infer<typeof feedbackFormSchema>) => {
    const { rating, comment, ...feedbackData } = values;
    createFeedbackMutation.mutate({
      ...feedbackData,
      feedback: {
        rating,
        comment,
        timestamp: new Date().toISOString(),
      },
    });
  };

  const renderStars = (rating: number) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`w-4 h-4 ${i < rating ? 'text-amber-400 fill-current' : 'text-slate-600'}`}
      />
    ));
  };

  if (feedbackLoading) {
    return (
      <div className="space-y-6">
        <Skeleton className="h-8 w-48" />
        <Card className="soc-card">
          <CardContent className="p-6">
            <Skeleton className="h-64 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <h2 className="text-2xl font-bold text-white" data-testid="feedback-title">Feedback & Reviews</h2>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button className="mt-4 sm:mt-0 soc-button-primary" data-testid="submit-feedback">
              <Plus className="w-4 h-4 mr-2" />
              Submit Feedback
            </Button>
          </DialogTrigger>
          <DialogContent className="bg-slate-800 border-slate-700">
            <DialogHeader>
              <DialogTitle className="text-white">Submit Feedback</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4" data-testid="feedback-form">
                <FormField
                  control={form.control}
                  name="incidentId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-200">Related Incident</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger className="bg-slate-700 border-slate-600 text-white" data-testid="incident-select">
                            <SelectValue placeholder="Select an incident" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {incidents?.slice(0, 10).map((incident: any) => (
                            <SelectItem key={incident.id} value={incident.id}>
                              #{incident.id.slice(-6)} - {new Date(incident.createdAt).toLocaleDateString()}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="rating"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-200">Rating</FormLabel>
                      <FormControl>
                        <div className="flex space-x-1" data-testid="rating-stars">
                          {Array.from({ length: 5 }, (_, i) => (
                            <button
                              key={i}
                              type="button"
                              onClick={() => field.onChange(i + 1)}
                              className="p-1"
                              data-testid={`star-${i + 1}`}
                            >
                              <Star
                                className={`w-6 h-6 ${
                                  i < field.value ? 'text-amber-400 fill-current' : 'text-slate-600'
                                }`}
                              />
                            </button>
                          ))}
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="comment"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-200">Comment</FormLabel>
                      <FormControl>
                        <Textarea
                          {...field}
                          placeholder="Share your thoughts on the incident handling, model accuracy, or system performance..."
                          className="bg-slate-700 border-slate-600 text-white min-h-[100px]"
                          data-testid="feedback-comment"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2 pt-4">
                  <Button 
                    type="button" 
                    variant="ghost" 
                    onClick={() => setIsCreateDialogOpen(false)}
                    data-testid="cancel-feedback"
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createFeedbackMutation.isPending}
                    className="soc-button-primary"
                    data-testid="submit-feedback-button"
                  >
                    {createFeedbackMutation.isPending ? "Submitting..." : "Submit Feedback"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Feedback Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <Card className="soc-card" data-testid="total-feedback-stat">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm font-medium">Total Feedback</p>
                <p className="text-3xl font-bold text-white mt-2" data-testid="total-feedback-count">
                  {feedback?.length || 0}
                </p>
              </div>
              <div className="w-12 h-12 bg-sky-500/10 rounded-lg flex items-center justify-center">
                <MessageSquare className="w-6 h-6 text-sky-500" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="soc-card" data-testid="avg-rating-stat">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm font-medium">Average Rating</p>
                <div className="flex items-center mt-2">
                  <p className="text-3xl font-bold text-white mr-2" data-testid="avg-rating-value">4.2</p>
                  <div className="flex">
                    {renderStars(4)}
                  </div>
                </div>
              </div>
              <div className="w-12 h-12 bg-amber-500/10 rounded-lg flex items-center justify-center">
                <Star className="w-6 h-6 text-amber-500" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="soc-card" data-testid="positive-feedback-stat">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm font-medium">Positive Feedback</p>
                <p className="text-3xl font-bold text-white mt-2" data-testid="positive-feedback-percentage">
                  86%
                </p>
              </div>
              <div className="w-12 h-12 bg-emerald-500/10 rounded-lg flex items-center justify-center">
                <MessageSquare className="w-6 h-6 text-emerald-500" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="soc-card" data-testid="response-rate-stat">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-400 text-sm font-medium">Response Rate</p>
                <p className="text-3xl font-bold text-white mt-2" data-testid="feedback-response-rate">
                  73%
                </p>
              </div>
              <div className="w-12 h-12 bg-sky-500/10 rounded-lg flex items-center justify-center">
                <User className="w-6 h-6 text-sky-500" />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Feedback List */}
      <Card className="soc-card" data-testid="feedback-list">
        <CardHeader>
          <CardTitle className="text-lg font-medium text-white">
            Recent Feedback ({feedback?.length || 0})
          </CardTitle>
        </CardHeader>
        <CardContent>
          {!feedback?.length ? (
            <div className="text-center py-12 text-slate-400">
              <MessageSquare className="w-12 h-12 mx-auto mb-4 text-slate-600" />
              <p>No feedback submitted yet</p>
              <p className="text-sm mt-2">Be the first to share your thoughts!</p>
            </div>
          ) : (
            <div className="space-y-4">
              {feedback.map((item: any, index: number) => (
                <div key={item.id} className="flex space-x-4 p-4 bg-slate-750 rounded-lg" data-testid={`feedback-${index}`}>
                  <div className="w-10 h-10 bg-slate-600 rounded-full flex items-center justify-center flex-shrink-0">
                    <User className="w-5 h-5 text-slate-300" />
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <span className="font-medium text-white" data-testid={`feedback-${index}-user`}>
                        Security Analyst
                      </span>
                      <span className="text-slate-400 text-sm" data-testid={`feedback-${index}-time`}>
                        ‚Ä¢ {new Date(item.submittedAt).toLocaleString()}
                      </span>
                    </div>
                    {item.feedback?.comment && (
                      <p className="text-slate-300 text-sm mb-3" data-testid={`feedback-${index}-comment`}>
                        {item.feedback.comment}
                      </p>
                    )}
                    <div className="flex items-center space-x-4">
                      <div className="flex items-center space-x-1">
                        <span className="text-xs text-slate-400">Incident:</span>
                        <Badge variant="outline" className="text-sky-400 border-sky-400" data-testid={`feedback-${index}-incident`}>
                          #{item.incidentId?.slice(-6) || 'N/A'}
                        </Badge>
                      </div>
                      {item.feedback?.rating && (
                        <div className="flex items-center space-x-1" data-testid={`feedback-${index}-rating`}>
                          <span className="text-xs text-slate-400">Rating:</span>
                          <div className="flex">
                            {renderStars(item.feedback.rating)}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/incidents.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { AlertTriangle, Eye, UserPlus, TrendingUp, Search, Download, X, CheckCircle2 } from "lucide-react";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Skeleton } from "@/components/ui/skeleton";
import { ExportDialog } from "@/components/export-dialog";
import { useState } from "react";

export default function Incidents() {
  const { toast } = useToast();
  const [filterSeverity, setFilterSeverity] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [selectedIncidents, setSelectedIncidents] = useState<string[]>([]);
  const [bulkOperation, setBulkOperation] = useState<string>('');
  const [showBulkDialog, setShowBulkDialog] = useState(false);

  const { data: incidents, isLoading } = useQuery({
    queryKey: ['/api/incidents'],
    queryFn: api.getIncidents,
  });

  const updateIncidentMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: any }) => api.updateIncident(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/incidents'] });
      toast({
        title: "Success",
        description: "Incident updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update incident",
        variant: "destructive",
      });
    },
  });

  const createActionMutation = useMutation({
    mutationFn: ({ incidentId, action }: { incidentId: string; action: any }) => 
      api.createAction(incidentId, action),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/incidents'] });
      toast({
        title: "Success",
        description: "Action created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create action",
        variant: "destructive",
      });
    },
  });

  const bulkUpdateMutation = useMutation({
    mutationFn: ({ incidentIds, operation, data }: { incidentIds: string[]; operation: string; data?: any }) =>
      api.bulkUpdateIncidents(incidentIds, operation, data),
    onSuccess: (result) => {
      queryClient.invalidateQueries({ queryKey: ['/api/incidents'] });
      setSelectedIncidents([]);
      setShowBulkDialog(false);
      toast({
        title: "Bulk Operation Complete",
        description: `Updated ${result.updated} incidents${result.errorCount > 0 ? ` (${result.errorCount} errors)` : ''}`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to perform bulk operation",
        variant: "destructive",
      });
    },
  });

  const filteredIncidents = incidents?.filter((incident: any) => {
    const severityMatch = filterSeverity === 'all' || 
      (filterSeverity === 'critical' && incident.confidence > 0.8) ||
      (filterSeverity === 'high' && incident.confidence <= 0.8 && incident.confidence > 0.5) ||
      (filterSeverity === 'medium' && incident.confidence <= 0.5 && incident.confidence > 0.3) ||
      (filterSeverity === 'low' && incident.confidence <= 0.3);

    const searchMatch = searchTerm === '' || 
      incident.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
      incident.status.toLowerCase().includes(searchTerm.toLowerCase());

    return severityMatch && searchMatch;
  }) || [];

  const getSeverityInfo = (confidence: number) => {
    if (confidence > 0.8) return { label: 'Critical', color: 'critical' };
    if (confidence > 0.5) return { label: 'High', color: 'high' };
    if (confidence > 0.3) return { label: 'Medium', color: 'medium' };
    return { label: 'Low', color: 'low' };
  };

  const handleAssignIncident = (incidentId: string) => {
    createActionMutation.mutate({
      incidentId,
      action: {
        actionType: 'ANALYST_ASSIGNMENT',
        payload: { assignedTo: 'current_user' },
        performedBy: 'system',
      },
    });
  };

  const handleEscalateIncident = (incidentId: string) => {
    createActionMutation.mutate({
      incidentId,
      action: {
        actionType: 'ESCALATE',
        payload: { escalatedTo: 'senior_analyst' },
        performedBy: 'system',
      },
    });
  };

  const handleSelectIncident = (incidentId: string, checked: boolean) => {
    if (checked) {
      setSelectedIncidents(prev => [...prev, incidentId]);
    } else {
      setSelectedIncidents(prev => prev.filter(id => id !== incidentId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedIncidents(filteredIncidents.map((inc: any) => inc.id));
    } else {
      setSelectedIncidents([]);
    }
  };

  const handleBulkOperation = (operation: string) => {
    setBulkOperation(operation);
    setShowBulkDialog(true);
  };

  const confirmBulkOperation = () => {
    if (selectedIncidents.length === 0 || !bulkOperation) return;

    const data: any = { performedBy: 'analyst' };

    if (bulkOperation === 'assign') {
      data.assignedTo = 'current_user'; // In real app, this would be selected
    } else if (bulkOperation === 'close') {
      data.reason = 'Bulk closure by analyst';
    }

    bulkUpdateMutation.mutate({
      incidentIds: selectedIncidents,
      operation: bulkOperation,
      data
    });
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <Skeleton className="h-8 w-48" />
        <Card className="soc-card">
          <CardContent className="p-6">
            <Skeleton className="h-64 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div className="flex items-center gap-4">
          <h2 className="text-2xl font-bold text-white" data-testid="incidents-title">Incident Management</h2>
          {selectedIncidents.length > 0 && (
            <div className="flex items-center gap-2">
              <Badge variant="secondary" className="text-xs">
                {selectedIncidents.length} selected
              </Badge>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setSelectedIncidents([])}
                className="text-slate-400 hover:text-white"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          )}
        </div>
        <div className="flex flex-wrap gap-3">
          {selectedIncidents.length > 0 && (
            <>
              <Button
                onClick={() => handleBulkOperation('assign')}
                disabled={bulkUpdateMutation.isPending}
                className="soc-button-secondary"
                data-testid="bulk-assign"
              >
                <UserPlus className="w-4 h-4 mr-2" />
                Assign ({selectedIncidents.length})
              </Button>
              <Button
                onClick={() => handleBulkOperation('escalate')}
                disabled={bulkUpdateMutation.isPending}
                className="soc-button-warning"
                data-testid="bulk-escalate"
              >
                <TrendingUp className="w-4 h-4 mr-2" />
                Escalate ({selectedIncidents.length})
              </Button>
              <Button
                onClick={() => handleBulkOperation('close')}
                disabled={bulkUpdateMutation.isPending}
                className="soc-button-success"
                data-testid="bulk-close"
              >
                <CheckCircle2 className="w-4 h-4 mr-2" />
                Close ({selectedIncidents.length})
              </Button>
            </>
          )}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-slate-400" />
            <Input
              type="text"
              placeholder="Search incidents..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="bg-slate-700 border-slate-600 pl-10 text-white placeholder-slate-400 w-64"
              data-testid="incidents-search"
            />
          </div>
          <Select value={filterSeverity} onValueChange={setFilterSeverity}>
            <SelectTrigger className="bg-slate-700 border-slate-600 text-white w-40" data-testid="severity-filter">
              <SelectValue placeholder="All Incidents" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Incidents</SelectItem>
              <SelectItem value="critical">Critical</SelectItem>
              <SelectItem value="high">High</SelectItem>
              <SelectItem value="medium">Medium</SelectItem>
              <SelectItem value="low">Low</SelectItem>
            </SelectContent>
          </Select>
          <ExportDialog 
            type="incidents" 
            triggerText="Export Report"
            className="soc-button-primary"
          />
        </div>
      </div>

      {/* Incidents Table */}
      <Card className="soc-card" data-testid="incidents-table">
        <CardHeader>
          <CardTitle className="text-lg font-medium text-white">
            Active Incidents ({filteredIncidents.length})
          </CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          {filteredIncidents.length === 0 ? (
            <div className="text-center py-12 text-slate-400">
              <AlertTriangle className="w-12 h-12 mx-auto mb-4 text-slate-600" />
              <p>No incidents found matching your criteria</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-slate-750">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-400 uppercase tracking-wider w-12">
                      <Checkbox
                        checked={selectedIncidents.length === filteredIncidents.length && filteredIncidents.length > 0}
                        onCheckedChange={handleSelectAll}
                        data-testid="select-all-checkbox"
                      />
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-400 uppercase tracking-wider">ID</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-400 uppercase tracking-wider">Description</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-400 uppercase tracking-wider">Severity</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-400 uppercase tracking-wider">Status</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-400 uppercase tracking-wider">Created</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-slate-400 uppercase tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-slate-700">
                  {filteredIncidents.map((incident: any, index: number) => {
                    const severity = getSeverityInfo(incident.confidence);
                    return (
                      <tr key={incident.id} className="hover:bg-slate-750" data-testid={`incident-row-${index}`}>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <Checkbox
                            checked={selectedIncidents.includes(incident.id)}
                            onCheckedChange={(checked) => handleSelectIncident(incident.id, checked as boolean)}
                            data-testid={`select-incident-${index}`}
                          />
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-mono text-slate-300" data-testid={`incident-id-${index}`}>
                          #{incident.id.slice(-6)}
                        </td>
                        <td className="px-6 py-4 text-sm text-white">
                          <div>
                            <p className="font-medium" data-testid={`incident-title-${index}`}>
                              Security Incident Detection
                            </p>
                            <p className="text-slate-400" data-testid={`incident-description-${index}`}>
                              Automated detection with {(incident.confidence * 100).toFixed(1)}% confidence
                            </p>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <Badge className={`soc-status-${severity.color}`} data-testid={`incident-severity-${index}`}>
                            {severity.label}
                          </Badge>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <Badge 
                            variant={incident.status === 'open' ? 'destructive' : 'secondary'}
                            data-testid={`incident-status-${index}`}
                          >
                            {incident.status}
                          </Badge>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-slate-300" data-testid={`incident-created-${index}`}>
                          {new Date(incident.createdAt).toLocaleString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm space-x-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            className="text-sky-400 hover:text-sky-300"
                            data-testid={`view-incident-${index}`}
                          >
                            <Eye className="w-4 h-4 mr-1" />
                            View
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="text-emerald-400 hover:text-emerald-300"
                            onClick={() => handleAssignIncident(incident.id)}
                            disabled={createActionMutation.isPending}
                            data-testid={`assign-incident-${index}`}
                          >
                            <UserPlus className="w-4 h-4 mr-1" />
                            Assign
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="text-red-400 hover:text-red-300"
                            onClick={() => handleEscalateIncident(incident.id)}
                            disabled={createActionMutation.isPending}
                            data-testid={`escalate-incident-${index}`}
                          >
                            <TrendingUp className="w-4 h-4 mr-1" />
                            Escalate
                          </Button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Bulk Operation Confirmation Dialog */}
      <Dialog open={showBulkDialog} onOpenChange={setShowBulkDialog}>
        <DialogContent className="bg-slate-800 border-slate-700">
          <DialogHeader>
            <DialogTitle className="text-white">Confirm Bulk Operation</DialogTitle>
            <DialogDescription className="text-slate-400">
              You are about to {bulkOperation} {selectedIncidents.length} incident{selectedIncidents.length !== 1 ? 's' : ''}. 
              This action cannot be undone.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div className="bg-slate-750 p-4 rounded-lg">
              <h4 className="text-white font-medium mb-2">Selected Incidents:</h4>
              <div className="space-y-1 max-h-32 overflow-y-auto">
                {selectedIncidents.map((incidentId, index) => {
                  const incident = filteredIncidents.find((inc: any) => inc.id === incidentId);
                  return (
                    <div key={incidentId} className="text-sm text-slate-300">
                      #{incident?.id.slice(-6)} - {incident?.status || 'Unknown'}
                    </div>
                  );
                })}
              </div>
            </div>
            <div className="flex justify-end space-x-2">
              <Button
                variant="ghost"
                onClick={() => setShowBulkDialog(false)}
                disabled={bulkUpdateMutation.isPending}
              >
                Cancel
              </Button>
              <Button
                onClick={confirmBulkOperation}
                disabled={bulkUpdateMutation.isPending}
                className="soc-button-primary"
                data-testid="confirm-bulk-operation"
              >
                {bulkUpdateMutation.isPending ? "Processing..." : `Confirm ${bulkOperation}`}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { AlertTriangle, Shield, Eye, EyeOff, LogIn } from "lucide-react";
import { useLocation } from "wouter";
import { api } from "@/lib/api";
import { Badge } from "@/components/ui/badge";

export default function Login() {
  const [, setLocation] = useLocation();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  const demoUsers = [
    { email: "john.smith@company.com", password: "password123", role: "Analyst", name: "John Smith" },
    { email: "sarah.johnson@company.com", password: "admin123", role: "Admin", name: "Sarah Johnson" },
    { email: "mike.wilson@company.com", password: "password123", role: "Analyst", name: "Mike Wilson" }
  ];

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!email || !password) {
      setError("Please enter both email and password");
      return;
    }

    setIsLoading(true);
    setError("");

    try {
      await api.login({ email, password });
      // Redirect to dashboard on success
      setLocation("/dashboard");
    } catch (err: any) {
      setError(err.message || "Login failed");
    } finally {
      setIsLoading(false);
    }
  };

  const quickLogin = (user: typeof demoUsers[0]) => {
    setEmail(user.email);
    setPassword(user.password);
  };

  return (
    <div className="min-h-screen bg-[hsl(215,28%,5%)] text-white font-['Inter'] flex items-center justify-center p-6">
      {/* Background Effect */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-[hsl(330,100%,50%)]/10 via-transparent to-[hsl(267,100%,67%)]/10"></div>
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-[hsl(330,100%,50%)]/5 rounded-full blur-3xl"></div>
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-[hsl(267,100%,67%)]/5 rounded-full blur-3xl"></div>
      </div>

      {/* Login Card */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="relative z-10 w-full max-w-md"
      >
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center space-x-2 mb-4">
            <div className="w-12 h-12 bg-gradient-to-r from-[hsl(330,100%,50%)] to-[hsl(267,100%,67%)] rounded-full flex items-center justify-center">
              <Shield className="w-6 h-6 text-white" />
            </div>
            <span className="text-2xl font-bold">Alpha SOC</span>
          </div>
          <h1 className="text-2xl font-bold text-white mb-2">Security Operations Center</h1>
          <p className="text-gray-400">Sign in to access your cybersecurity dashboard</p>
        </div>

        {/* Login Form */}
        <Card className="bg-[hsl(0,0%,8%)]/80 border-[hsl(330,100%,50%)]/20 backdrop-blur-sm">
          <CardHeader>
            <CardTitle className="text-white">Sign In</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleLogin} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="email" className="text-gray-300">Email</Label>
                <Input
                  id="email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="bg-[hsl(0,0%,10%)]/60 border-[hsl(330,100%,50%)]/20 text-white placeholder:text-gray-400"
                  placeholder="Enter your email"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="password" className="text-gray-300">Password</Label>
                <div className="relative">
                  <Input
                    id="password"
                    type={showPassword ? "text" : "password"}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="bg-[hsl(0,0%,10%)]/60 border-[hsl(330,100%,50%)]/20 text-white placeholder:text-gray-400 pr-10"
                    placeholder="Enter your password"
                    required
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="icon"
                    className="absolute right-0 top-0 h-full px-3 text-gray-400 hover:text-white"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                  </Button>
                </div>
              </div>

              {error && (
                <div className="flex items-center space-x-2 p-3 bg-red-500/10 border border-red-500/20 rounded-lg">
                  <AlertTriangle className="w-4 h-4 text-red-400" />
                  <span className="text-red-400 text-sm">{error}</span>
                </div>
              )}

              <Button
                type="submit"
                disabled={isLoading}
                className="w-full bg-[hsl(330,100%,50%)] hover:bg-[hsl(330,100%,60%)] text-white"
              >
                {isLoading ? (
                  <div className="flex items-center space-x-2">
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                    <span>Signing in...</span>
                  </div>
                ) : (
                  <div className="flex items-center space-x-2">
                    <LogIn className="w-4 h-4" />
                    <span>Sign In</span>
                  </div>
                )}
              </Button>
            </form>
          </CardContent>
        </Card>

        {/* Demo Accounts */}
        <Card className="mt-6 bg-[hsl(0,0%,8%)]/60 border-[hsl(330,100%,50%)]/10">
          <CardHeader>
            <CardTitle className="text-sm text-gray-300">Demo Accounts</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {demoUsers.map((user, index) => (
                <div
                  key={index}
                  className="flex items-center justify-between p-3 bg-[hsl(0,0%,6%)]/60 rounded-lg border border-[hsl(330,100%,50%)]/10 cursor-pointer hover:bg-[hsl(0,0%,10%)]/60 transition-colors"
                  onClick={() => quickLogin(user)}
                >
                  <div>
                    <div className="text-white font-medium">{user.name}</div>
                    <div className="text-xs text-gray-400">{user.email}</div>
                  </div>
                  <Badge className={user.role === 'Admin' ? 'bg-purple-500/20 text-purple-400' : 'bg-blue-500/20 text-blue-400'}>
                    {user.role}
                  </Badge>
                </div>
              ))}
            </div>
            <p className="text-xs text-gray-500 mt-3">Click any account to auto-fill credentials</p>
          </CardContent>
        </Card>

        {/* Footer */}
        <div className="text-center mt-6">
          <p className="text-xs text-gray-500">
            Alpha SOC Platform ‚Ä¢ Secure ‚Ä¢ Real-time Threat Detection
          </p>
        </div>
      </motion.div>
    </div>
  );
}

