import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Shield, Server, Plus, Edit, Trash2, Activity } from "lucide-react";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { SourceCard } from "@/components/source-card";
import { Skeleton } from "@/components/ui/skeleton";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { insertSourceSchema } from "@shared/schema";
import { useState } from "react";

const sourceFormSchema = insertSourceSchema.extend({
  endpoint: z.string().url().optional(),
});

export default function Sources() {
  const { toast } = useToast();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);

  const { data: sources, isLoading } = useQuery({
    queryKey: ['/api/sources'],
    queryFn: api.getSources,
  });

  const createSourceMutation = useMutation({
    mutationFn: api.createSource,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/sources'] });
      setIsCreateDialogOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Source created successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create source",
        variant: "destructive",
      });
    },
  });

  const updateSourceMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: any }) => api.updateSource(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/sources'] });
      toast({
        title: "Success",
        description: "Source updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update source",
        variant: "destructive",
      });
    },
  });

  const deleteSourceMutation = useMutation({
    mutationFn: api.deleteSource,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/sources'] });
      toast({
        title: "Success",
        description: "Source deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete source",
        variant: "destructive",
      });
    },
  });

  const form = useForm<z.infer<typeof sourceFormSchema>>({
    resolver: zodResolver(sourceFormSchema),
    defaultValues: {
      name: "",
      type: "",
      config: {},
    },
  });

  const onSubmit = (values: z.infer<typeof sourceFormSchema>) => {
    const { endpoint, ...sourceData } = values;
    const config = endpoint ? { ...(sourceData.config || {}), endpoint } : (sourceData.config || {});

    createSourceMutation.mutate({
      ...sourceData,
      config,
    });
  };

  const handleToggleSource = (sourceId: string, currentStatus: boolean) => {
    updateSourceMutation.mutate({
      id: sourceId,
      data: { config: { enabled: !currentStatus } },
    });
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <Skeleton className="h-8 w-48" />
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Card key={i} className="soc-card">
              <CardContent className="p-6">
                <Skeleton className="h-48 w-full" />
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
        <h2 className="text-2xl font-bold text-white" data-testid="sources-title">Data Sources</h2>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button className="mt-4 sm:mt-0 soc-button-primary" data-testid="add-source">
              <Plus className="w-4 h-4 mr-2" />
              Add Source
            </Button>
          </DialogTrigger>
          <DialogContent className="bg-slate-800 border-slate-700">
            <DialogHeader>
              <DialogTitle className="text-white">Add New Data Source</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4" data-testid="create-source-form">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-200">Source Name</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          className="bg-slate-700 border-slate-600 text-white"
                          data-testid="source-name-input"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="type"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-200">Source Type</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger className="bg-slate-700 border-slate-600 text-white" data-testid="source-type-select">
                            <SelectValue placeholder="Select source type" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="SIEM">SIEM</SelectItem>
                          <SelectItem value="EDR">EDR</SelectItem>
                          <SelectItem value="Firewall">Firewall</SelectItem>
                          <SelectItem value="IDS">IDS</SelectItem>
                          <SelectItem value="Antivirus">Antivirus</SelectItem>
                          <SelectItem value="Network">Network Monitor</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="endpoint"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-slate-200">Endpoint URL (Optional)</FormLabel>
                      <FormControl>
                        <Input 
                          {...field} 
                          placeholder="https://source.example.com/api"
                          className="bg-slate-700 border-slate-600 text-white"
                          data-testid="source-endpoint-input"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2 pt-4">
                  <Button 
                    type="button" 
                    variant="ghost" 
                    onClick={() => setIsCreateDialogOpen(false)}
                    data-testid="cancel-source-button"
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createSourceMutation.isPending}
                    className="soc-button-primary"
                    data-testid="create-source-button"
                  >
                    {createSourceMutation.isPending ? "Creating..." : "Create Source"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Sources Grid */}
      {sources?.length === 0 ? (
        <Card className="soc-card">
          <CardContent className="text-center py-12">
            <Server className="w-12 h-12 mx-auto mb-4 text-slate-600" />
            <p className="text-slate-400 text-lg mb-2">No data sources configured</p>
            <p className="text-slate-500 text-sm">Add your first data source to start monitoring</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {sources?.map((source: any, index: number) => (
            <SourceCard
              key={source.id}
              source={source}
              onToggle={(enabled) => handleToggleSource(source.id, !enabled)}
              onDelete={() => deleteSourceMutation.mutate(source.id)}
              isDeleting={deleteSourceMutation.isPending}
              isToggling={updateSourceMutation.isPending}
              testId={`source-${index}`}
            />
          ))}
        </div>
      )}
    </div>
  );
}

client/src/components/layout/header.tsx.content:
import { Search, Bell, Menu, LogOut, User } from "lucide-react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import type { User as UserType } from "@shared/schema";

interface HeaderProps {
  onMobileMenuToggle: () => void;
  connectionStatus: 'connecting' | 'connected' | 'disconnected';
  user?: UserType;
}

export function Header({ onMobileMenuToggle, connectionStatus, user }: HeaderProps) {
  const handleLogout = async () => {
    try {
      await fetch('/api/auth/logout', {
        method: 'POST',
        credentials: 'include',
      });
      // Reload page to trigger authentication check
      window.location.reload();
    } catch (error) {
      console.error('Logout error:', error);
      // Fallback: reload anyway
      window.location.reload();
    }
  };

  const getUserInitials = (user: UserType) => {
    const firstName = user.firstName || '';
    const lastName = user.lastName || '';
    return (firstName.charAt(0) + lastName.charAt(0)).toUpperCase() || user.email?.charAt(0).toUpperCase() || 'U';
  };
  return (
    <header className="bg-slate-800 border-b border-slate-700 px-4 lg:px-8 py-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center">
          {/* Mobile menu button */}
          <Button
            variant="ghost"
            size="icon"
            onClick={onMobileMenuToggle}
            className="lg:hidden mr-4 text-slate-400 hover:text-white hover:bg-slate-700"
            data-testid="mobile-menu-button"
          >
            <Menu className="w-6 h-6" />
          </Button>

          <div>
            <h1 className="text-2xl font-semibold text-white">Security Operations Dashboard</h1>
            <p className="text-sm text-slate-400 mt-1">Real-time monitoring and incident response</p>
          </div>
        </div>

        <div className="flex items-center space-x-4">
          {/* Real-time status indicator */}
          <div className="flex items-center space-x-2" data-testid="connection-status">
            <div 
              className={`w-2 h-2 rounded-full ${
                connectionStatus === 'connected' ? 'bg-green-500 animate-pulse' :
                connectionStatus === 'connecting' ? 'bg-amber-500 animate-pulse' :
                'bg-red-500'
              }`} 
            />
            <span className="text-sm text-slate-300 capitalize">{connectionStatus}</span>
          </div>

          {/* Search */}
          <div className="hidden md:block">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-slate-400" />
              <Input
                type="text"
                placeholder="Search incidents..."
                className="bg-slate-700 border-slate-600 pl-10 pr-4 text-white placeholder-slate-400 focus:ring-sky-500 focus:border-transparent w-64"
                data-testid="search-input"
              />
            </div>
          </div>

          {/* Notifications */}
          <Button
            variant="ghost"
            size="icon"
            className="relative text-slate-400 hover:text-white"
            data-testid="notifications-button"
          >
            <Bell className="w-6 h-6" />
            <span className="absolute top-0 right-0 block h-2 w-2 bg-red-400 rounded-full"></span>
          </Button>

          {/* User Profile */}
          {user && (
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" className="relative h-8 w-8 rounded-full">
                  <Avatar className="h-8 w-8">
                    <AvatarImage src={user.profileImageUrl || undefined} alt="Profile" />
                    <AvatarFallback className="bg-slate-600 text-white text-sm">
                      {getUserInitials(user)}
                    </AvatarFallback>
                  </Avatar>
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent className="w-56 bg-slate-800 border-slate-700" align="end" forceMount>
                <DropdownMenuLabel className="font-normal">
                  <div className="flex flex-col space-y-1">
                    <p className="text-sm font-medium leading-none text-white">
                      {user.firstName || user.lastName 
                        ? `${user.firstName || ''} ${user.lastName || ''}`.trim()
                        : 'User'
                      }
                    </p>
                    <p className="text-xs leading-none text-slate-400">
                      {user.email}
                    </p>
                  </div>
                </DropdownMenuLabel>
                <DropdownMenuSeparator className="bg-slate-700" />
                <DropdownMenuItem 
                  className="text-slate-300 hover:bg-slate-700 hover:text-white cursor-pointer"
                  onClick={handleLogout}
                >
                  <LogOut className="mr-2 h-4 w-4" />
                  <span>Log out</span>
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          )}
        </div>
      </div>
    </header>
  );
}

client/src/components/layout/sidebar.tsx.content:
import { Shield, BarChart3, AlertTriangle, Server, TrendingUp, MessageSquare } from "lucide-react";
import { cn } from "@/lib/utils";

interface SidebarProps {
  currentSection: string;
  onSectionChange: (section: string) => void;
  className?: string;
}

const navigationItems = [
  {
    id: 'dashboard',
    label: 'Dashboard',
    icon: BarChart3,
    badge: null,
  },
  {
    id: 'incidents',
    label: 'Incidents',
    icon: AlertTriangle,
    badge: { count: 5, variant: 'destructive' as const },
  },
  {
    id: 'alerts',
    label: 'Alerts',
    icon: Shield,
    badge: { count: 23, variant: 'warning' as const },
  },
  {
    id: 'sources',
    label: 'Sources',
    icon: Server,
    badge: null,
  },
  {
    id: 'analytics',
    label: 'Analytics',
    icon: TrendingUp,
    badge: null,
  },
  {
    id: 'feedback',
    label: 'Feedback',
    icon: MessageSquare,
    badge: null,
  },
];

export function Sidebar({ currentSection, onSectionChange, className }: SidebarProps) {
  return (
    <nav className={cn("flex flex-col w-full", className)}>
      {/* Logo */}
      <div className="flex items-center px-6 py-4 border-b border-slate-700">
        <div className="w-8 h-8 bg-sky-500 rounded-lg flex items-center justify-center">
          <Shield className="w-5 h-5 text-white" />
        </div>
        <span className="ml-3 text-xl font-semibold text-white">SOC Platform</span>
      </div>

      {/* Navigation Menu */}
      <div className="flex-1 px-3 py-4 space-y-1">
        {navigationItems.map((item) => (
          <button
            key={item.id}
            onClick={() => onSectionChange(item.id)}
            data-testid={`nav-${item.id}`}
            className={cn(
              "flex items-center w-full px-3 py-2 text-sm font-medium rounded-md group transition-colors",
              currentSection === item.id
                ? "bg-slate-700 text-white"
                : "text-slate-300 hover:bg-slate-700 hover:text-white"
            )}
          >
            <item.icon className="mr-3 w-5 h-5" />
            {item.label}
            {item.badge && (
              <span
                className={cn(
                  "ml-auto text-xs rounded-full px-2 py-1",
                  item.badge.variant === 'destructive' && "bg-red-500 text-white",
                  item.badge.variant === 'warning' && "bg-amber-500 text-white"
                )}
                data-testid={`badge-${item.id}`}
              >
                {item.badge.count}
              </span>
            )}
          </button>
        ))}
      </div>

      {/* User Profile */}
      <div className="px-3 py-4 border-t border-slate-700">
        <div className="flex items-center">
          <div className="w-8 h-8 bg-slate-600 rounded-full flex items-center justify-center">
            <span className="text-sm font-medium text-white">JS</span>
          </div>
          <div className="ml-3">
            <p className="text-sm font-medium text-white">John Smith</p>
            <p className="text-xs text-slate-400">Security Analyst</p>
          </div>
        </div>
      </div>
    </nav>
  );
}

