            <FormField
              control={form.control}
              name="company"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-white">Company *</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="ACME Corp" 
                      {...field} 
                      className="bg-[hsl(0,0%,10%)]/50 border-[hsl(170,100%,48%)]/30 text-white placeholder:text-gray-400 focus:border-[hsl(170,100%,48%)]"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="jobTitle"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-white">Job Title</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="CISO" 
                        {...field} 
                        className="bg-[hsl(0,0%,10%)]/50 border-[hsl(170,100%,48%)]/30 text-white placeholder:text-gray-400 focus:border-[hsl(170,100%,48%)]"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="phoneNumber"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-white">Phone Number</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="+1 (555) 123-4567" 
                        {...field} 
                        className="bg-[hsl(0,0%,10%)]/50 border-[hsl(170,100%,48%)]/30 text-white placeholder:text-gray-400 focus:border-[hsl(170,100%,48%)]"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-white">Password *</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢" 
                        type="password"
                        {...field} 
                        className="bg-[hsl(0,0%,10%)]/50 border-[hsl(170,100%,48%)]/30 text-white placeholder:text-gray-400 focus:border-[hsl(170,100%,48%)]"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="confirmPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-white">Confirm Password *</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢" 
                        type="password"
                        {...field} 
                        className="bg-[hsl(0,0%,10%)]/50 border-[hsl(170,100%,48%)]/30 text-white placeholder:text-gray-400 focus:border-[hsl(170,100%,48%)]"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            {/* Password Requirements */}
            {password && (
              <motion.div
                initial={{ opacity: 0, height: 0 }}
                animate={{ opacity: 1, height: "auto" }}
                className="bg-[hsl(0,0%,10%)]/30 rounded-lg p-3 border border-[hsl(170,100%,48%)]/20"
              >
                <div className="text-sm text-gray-300 mb-2">Password Requirements:</div>
                <div className="grid grid-cols-2 gap-2">
                  {passwordCriteria.map((criteria, index) => (
                    <motion.div
                      key={index}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                      className={`flex items-center gap-2 text-xs ${
                        criteria.test(password) ? "text-green-400" : "text-gray-500"
                      }`}
                    >
                      {criteria.test(password) ? (
                        <CheckCircle2 className="w-3 h-3" />
                      ) : (
                        <AlertCircle className="w-3 h-3" />
                      )}
                      {criteria.label}
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}

            <FormField
              control={form.control}
              name="message"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-white">Message</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Tell us about your security requirements..." 
                      rows={3}
                      {...field} 
                      className="bg-[hsl(0,0%,10%)]/50 border-[hsl(170,100%,48%)]/30 text-white placeholder:text-gray-400 focus:border-[hsl(170,100%,48%)] resize-none"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                className="flex-1 bg-transparent border-gray-600 text-gray-300 hover:bg-gray-800"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                variant="glow"
                disabled={submitSignup.isPending || showBarriers}
                className="flex-1"
              >
                {submitSignup.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Creating Account...
                  </>
                ) : (
                  <>
                    <Shield className="w-4 h-4 mr-2" />
                    Create Account
                  </>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

