import { Progress } from "@/components/ui/progress";

type AlertSeverity = "Critical" | "High" | "Medium" | "Low";
type AlertStatus = "Active" | "Investigating" | "Resolved";

interface Alert {
  id: string;
  severity: AlertSeverity;
  status: AlertStatus;
  title: string;
  assignedAnalyst: string;
  time: string;
  source: string;
  confidence: number;
  aiRecommendation: string;
  rootCause?: string;
}

interface ModelResponse {
  id: string;
  timestamp: string;
  prediction: string;
  confidence: number;
  recommendation: string;
  status: "Processing" | "Complete" | "Failed";
  explainability: string;
}

interface AIInsight {
  type: "trend" | "anomaly" | "prediction" | "correlation";
  title: string;
  description: string;
  confidence: number;
  impact: "High" | "Medium" | "Low";
}

interface WorkflowAlert {
  id: string;
  vendorName: string;
  timestamp: string;
  severity: AlertSeverity;
  status: "incoming" | "processing" | "predicted" | "completed";
  stage: "vendor" | "ai_model" | "prediction" | "outcome";
  aiModel?: string;
  confidence?: number;
  processingTime?: number;
  outcome?: "TP" | "FP" | "Escalate";
  explanation?: string;
  escalationReason?: string;
  assignedAnalyst?: string;
}

interface EscalationItem {
  alertId: string;
  reason: string;
  assignedAnalyst: string;
  timestamp: string;
  status: "pending" | "approved" | "rejected";
}

interface AutomationMetrics {
  tpHandled: number;
  fpDismissed: number;
  totalProcessed: number;
  avgProcessingTime: number;
  confidence: {
    high: number;
    medium: number;
    low: number;
  };
}

const mockAlerts: Alert[] = [
  { 
