            whileHover={{ scale: 1.05 }}
          >
            Platform
          </motion.a>
          <motion.a 
            href="#solutions" 
            className="text-gray-300 hover:text-[hsl(330,100%,50%)] transition-all duration-300 font-medium"
            whileHover={{ scale: 1.05 }}
          >
            Solutions
          </motion.a>
          <motion.a 
            href="#resources" 
            className="text-gray-300 hover:text-[hsl(330,100%,50%)] transition-all duration-300 font-medium"
            whileHover={{ scale: 1.05 }}
          >
            Resources
          </motion.a>
          <motion.a 
