            <motion.a 
              href="#resources" 
              className="block hover:text-[hsl(330,100%,50%)] transition-colors text-lg font-medium"
              whileHover={{ x: 10 }}
            >
              Resources
            </motion.a>
            <motion.a 
              href="#company" 
              className="block hover:text-[hsl(330,100%,50%)] transition-colors text-lg font-medium"
              whileHover={{ x: 10 }}
