import { useState, useEffect, useRef, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  ZoomIn, 
  ZoomOut, 
  RotateCcw, 
  Play, 
  Pause, 
  Filter, 
  Search,
  Network,
  Shield,
  AlertTriangle,
  User,
  Server,
  Globe,
  Cpu,
  Database,
  Lock,
  Eye,
  FileText,
  Activity
} from "lucide-react";

interface GraphNode {
  id: string;
  label: string;
  type: "user" | "asset" | "ip" | "process" | "file" | "vulnerability" | "threat" | "connection" | "domain" | "service";
  risk: "critical" | "high" | "medium" | "low";
  x: number;
  y: number;
  vx: number;
  vy: number;
  radius: number;
  properties: Record<string, any>;
  connections: string[];
  isSelected: boolean;
  isHighlighted: boolean;
}

interface GraphEdge {
  id: string;
  source: string;
  target: string;
  type: "communicates" | "executes" | "accesses" | "exploits" | "contains" | "originates" | "authenticates";
  weight: number;
  isActive: boolean;
  properties: Record<string, any>;
}

interface GraphForce {
  attraction: number;
  repulsion: number;
  damping: number;
}

const nodeIcons = {
  user: User,
  asset: Server,
  ip: Globe,
  process: Cpu,
  file: FileText,
  vulnerability: AlertTriangle,
  threat: Shield,
  connection: Network,
  domain: Globe,
  service: Database
};

const nodeColors = {
  critical: "#EF4444",
  high: "#F97316", 
  medium: "#EAB308",
  low: "#10B981"
};

const edgeColors = {
  communicates: "#3B82F6",
  executes: "#8B5CF6",
  accesses: "#10B981",
  exploits: "#EF4444",
  contains: "#F59E0B",
  originates: "#06B6D4",
  authenticates: "#84CC16"
};

export function KnowledgeGraph() {
  const svgRef = useRef<SVGSVGElement>(null);
  const [nodes, setNodes] = useState<GraphNode[]>([]);
  const [edges, setEdges] = useState<GraphEdge[]>([]);
  const [selectedNode, setSelectedNode] = useState<GraphNode | null>(null);
  const [isSimulating, setIsSimulating] = useState(true);
  const [zoom, setZoom] = useState(1);
  const [pan, setPan] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [forces, setForces] = useState<GraphForce>({
    attraction: 0.1,
    repulsion: 100,
    damping: 0.95
  });
  const [searchTerm, setSearchTerm] = useState("");
  const [filterRisk, setFilterRisk] = useState<string>("all");

  // Initialize sample cybersecurity graph data
  useEffect(() => {
    const sampleNodes: GraphNode[] = [
      {
        id: "user-1",
        label: "john.doe@company.com",
        type: "user",
        risk: "medium",
        x: 300,
        y: 200,
        vx: 0,
        vy: 0,
        radius: 25,
        properties: { department: "Finance", lastLogin: "2024-01-07 14:30" },
        connections: ["asset-1", "process-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "asset-1",
        label: "WORKSTATION-001",
        type: "asset",
        risk: "high",
        x: 200,
        y: 150,
        vx: 0,
        vy: 0,
        radius: 30,
        properties: { os: "Windows 11", patch_level: "Critical patches missing" },
        connections: ["user-1", "ip-1", "vulnerability-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "ip-1",
        label: "192.168.1.105",
        type: "ip",
        risk: "critical",
        x: 100,
        y: 200,
        vx: 0,
        vy: 0,
        radius: 20,
        properties: { geo_location: "Internal", reputation: "Clean" },
        connections: ["asset-1", "threat-1", "connection-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "threat-1",
        label: "APT29 Campaign",
        type: "threat",
        risk: "critical",
        x: 50,
        y: 100,
        vx: 0,
        vy: 0,
        radius: 35,
        properties: { campaign: "SolarWinds-related", confidence: "High" },
        connections: ["ip-1", "vulnerability-1", "process-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "process-1",
        label: "malware.exe",
        type: "process",
        risk: "critical",
        x: 400,
        y: 150,
        vx: 0,
        vy: 0,
        radius: 22,
        properties: { pid: "1337", command_line: "powershell.exe -enc <base64>" },
        connections: ["user-1", "threat-1", "file-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "vulnerability-1",
        label: "CVE-2024-1234",
        type: "vulnerability",
        risk: "high",
        x: 150,
        y: 80,
        vx: 0,
        vy: 0,
        radius: 18,
        properties: { cvss_score: "8.9", description: "Remote code execution" },
        connections: ["asset-1", "threat-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "file-1",
        label: "suspicious.dll",
        type: "file",
        risk: "high",
        x: 450,
        y: 250,
        vx: 0,
        vy: 0,
        radius: 16,
        properties: { hash: "a1b2c3d4...", size: "2.3MB" },
        connections: ["process-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "connection-1",
        label: "C2 Channel",
        type: "connection",
        risk: "critical",
        x: 80,
        y: 300,
        vx: 0,
        vy: 0,
        radius: 20,
        properties: { protocol: "HTTPS", port: "443", encrypted: true },
        connections: ["ip-1", "domain-1"],
        isSelected: false,
        isHighlighted: false
      },
      {
        id: "domain-1",
        label: "evil-c2.com",
        type: "domain",
        risk: "critical",
        x: 180,
        y: 350,
        vx: 0,
        vy: 0,
        radius: 24,
        properties: { registrar: "Unknown", creation_date: "2024-01-01" },
        connections: ["connection-1"],
        isSelected: false,
        isHighlighted: false
      }
    ];

    const sampleEdges: GraphEdge[] = [
      {
        id: "edge-1",
        source: "user-1",
        target: "asset-1",
        type: "authenticates",
        weight: 1,
        isActive: true,
        properties: { login_time: "14:30", success: true }
      },
      {
        id: "edge-2",
        source: "asset-1",
        target: "ip-1",
        type: "communicates",
        weight: 2,
        isActive: true,
        properties: { bytes_transferred: "1.2GB", duration: "3m" }
      },
      {
        id: "edge-3",
        source: "ip-1",
        target: "threat-1",
        type: "originates",
        weight: 3,
        isActive: true,
        properties: { detection_time: "14:33", confidence: "95%" }
      },
      {
        id: "edge-4",
        source: "threat-1",
        target: "vulnerability-1",
        type: "exploits",
        weight: 3,
        isActive: true,
        properties: { exploit_kit: "Metasploit", success_rate: "87%" }
      },
      {
        id: "edge-5",
        source: "user-1",
        target: "process-1",
        type: "executes",
        weight: 2,
        isActive: true,
        properties: { execution_time: "14:35", elevated: false }
      },
      {
        id: "edge-6",
        source: "process-1",
        target: "file-1",
        type: "accesses",
        weight: 1,
        isActive: true,
        properties: { access_type: "write", timestamp: "14:36" }
      },
      {
        id: "edge-7",
        source: "ip-1",
        target: "connection-1",
        type: "communicates",
        weight: 2,
        isActive: true,
        properties: { protocol: "TLS 1.3", encrypted: true }
      },
      {
        id: "edge-8",
        source: "connection-1",
        target: "domain-1",
        type: "communicates",
        weight: 1,
        isActive: true,
        properties: { dns_resolution: "1.2.3.4", ttl: "300s" }
      }
    ];

    setNodes(sampleNodes);
    setEdges(sampleEdges);
  }, []);

  // Physics simulation
  const updateSimulation = useCallback(() => {
    if (!isSimulating) return;

    setNodes(currentNodes => {
      const newNodes = [...currentNodes];

      // Reset forces
      newNodes.forEach(node => {
        node.vx *= forces.damping;
        node.vy *= forces.damping;
      });

      // Repulsion between nodes
      for (let i = 0; i < newNodes.length; i++) {
        for (let j = i + 1; j < newNodes.length; j++) {
          const node1 = newNodes[i];
          const node2 = newNodes[j];

          const dx = node2.x - node1.x;
          const dy = node2.y - node1.y;
          const distance = Math.sqrt(dx * dx + dy * dy) || 1;

          const force = forces.repulsion / (distance * distance);
          const fx = (dx / distance) * force;
          const fy = (dy / distance) * force;

          node1.vx -= fx;
          node1.vy -= fy;
          node2.vx += fx;
          node2.vy += fy;
        }
      }

      // Attraction along edges
      edges.forEach(edge => {
        const source = newNodes.find(n => n.id === edge.source);
        const target = newNodes.find(n => n.id === edge.target);

        if (source && target) {
          const dx = target.x - source.x;
          const dy = target.y - source.y;
          const distance = Math.sqrt(dx * dx + dy * dy) || 1;
          const optimalDistance = 100;

          const force = forces.attraction * (distance - optimalDistance);
          const fx = (dx / distance) * force;
          const fy = (dy / distance) * force;

          source.vx += fx;
          source.vy += fy;
          target.vx -= fx;
          target.vy -= fy;
        }
      });

      // Update positions
      newNodes.forEach(node => {
        node.x += node.vx;
        node.y += node.vy;

        // Boundary constraints
        const margin = node.radius + 10;
        if (node.x < margin) { node.x = margin; node.vx = 0; }
        if (node.x > 580 - margin) { node.x = 580 - margin; node.vx = 0; }
        if (node.y < margin) { node.y = margin; node.vy = 0; }
        if (node.y > 380 - margin) { node.y = 380 - margin; node.vy = 0; }
      });

      return newNodes;
    });
  }, [isSimulating, forces, edges]);

  useEffect(() => {
    const interval = setInterval(updateSimulation, 50);
    return () => clearInterval(interval);
  }, [updateSimulation]);

  const handleNodeClick = (node: GraphNode) => {
    setSelectedNode(node);
    setNodes(nodes => 
      nodes.map(n => ({
        ...n,
        isSelected: n.id === node.id,
        isHighlighted: n.connections.includes(node.id) || n.id === node.id
      }))
    );
  };

  const handleZoom = (delta: number) => {
    setZoom(prev => Math.max(0.5, Math.min(3, prev + delta)));
  };

  const resetView = () => {
    setZoom(1);
    setPan({ x: 0, y: 0 });
    setSelectedNode(null);
    setNodes(nodes => nodes.map(n => ({ ...n, isSelected: false, isHighlighted: false })));
  };

  const filteredNodes = nodes.filter(node => {
    const matchesSearch = searchTerm === "" || 
      node.label.toLowerCase().includes(searchTerm.toLowerCase()) ||
      node.type.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesRisk = filterRisk === "all" || node.risk === filterRisk;

    return matchesSearch && matchesRisk;
  });

  const getNodeIcon = (type: string) => {
    const Icon = nodeIcons[type as keyof typeof nodeIcons] || Network;
    return Icon;
  };

  return (
    <div className="w-full h-full bg-[hsl(0,0%,8%)]/80 border border-[hsl(330,100%,50%)]/20 rounded-xl">
      {/* Controls Header */}
      <div className="p-4 border-b border-[hsl(330,100%,50%)]/20">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <Network className="w-5 h-5 text-[hsl(330,100%,50%)]" />
            <h3 className="text-lg font-semibold text-white">Deep Knowledge Graph</h3>
            <Badge variant="outline" className="border-[hsl(267,100%,67%)]/20 text-[hsl(267,100%,67%)]">
              Neo4j Style
            </Badge>
          </div>

          <div className="flex items-center space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsSimulating(!isSimulating)}
              className={`${isSimulating ? 'text-green-400' : 'text-gray-400'}`}
            >
              {isSimulating ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
            </Button>
            <Button variant="ghost" size="sm" onClick={() => handleZoom(0.1)}>
              <ZoomIn className="w-4 h-4" />
            </Button>
            <Button variant="ghost" size="sm" onClick={() => handleZoom(-0.1)}>
              <ZoomOut className="w-4 h-4" />
            </Button>
            <Button variant="ghost" size="sm" onClick={resetView}>
              <RotateCcw className="w-4 h-4" />
            </Button>
          </div>
        </div>

        <div className="flex items-center space-x-4">
          <div className="relative flex-1">
            <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              placeholder="Search nodes..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-[hsl(0,0%,10%)]/60 border border-[hsl(330,100%,50%)]/20 rounded-lg text-white placeholder:text-gray-400 focus:outline-none focus:border-[hsl(330,100%,50%)]/40"
            />
          </div>

          <select
            value={filterRisk}
            onChange={(e) => setFilterRisk(e.target.value)}
            className="px-3 py-2 bg-[hsl(0,0%,10%)]/60 border border-[hsl(330,100%,50%)]/20 rounded-lg text-white focus:outline-none focus:border-[hsl(330,100%,50%)]/40"
          >
            <option value="all">All Risk Levels</option>
            <option value="critical">Critical</option>
            <option value="high">High</option>
            <option value="medium">Medium</option>
            <option value="low">Low</option>
          </select>
        </div>
      </div>

      <div className="flex h-[600px]">
        {/* Graph Canvas */}
        <div className="flex-1 relative overflow-hidden">
          <svg
            ref={svgRef}
            className="w-full h-full"
            viewBox={`${-pan.x} ${-pan.y} ${600 / zoom} ${400 / zoom}`}
          >
            <defs>
              <filter id="glow" x="-50%" y="-50%" width="200%" height="200%">
                <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
                <feMerge>
                  <feMergeNode in="coloredBlur"/>
                  <feMergeNode in="SourceGraphic"/>
                </feMerge>
              </filter>

              <filter id="edge-glow" x="-50%" y="-50%" width="200%" height="200%">
                <feGaussianBlur stdDeviation="2" result="coloredBlur"/>
                <feMerge>
                  <feMergeNode in="coloredBlur"/>
                  <feMergeNode in="SourceGraphic"/>
                </feMerge>
              </filter>

              <marker
                id="arrowhead"
                markerWidth="10"
                markerHeight="7"
                refX="9"
                refY="3.5"
                orient="auto"
              >
                <polygon
                  points="0 0, 10 3.5, 0 7"
                  fill="#64748B"
                />
              </marker>
            </defs>

            {/* Edges */}
            {edges.map(edge => {
              const source = filteredNodes.find(n => n.id === edge.source);
              const target = filteredNodes.find(n => n.id === edge.target);

              if (!source || !target) return null;

              const isHighlighted = source.isHighlighted && target.isHighlighted;

              return (
                <g key={edge.id}>
                  <line
                    x1={source.x}
                    y1={source.y}
                    x2={target.x}
                    y2={target.y}
                    stroke={isHighlighted ? edgeColors[edge.type] : "#374151"}
                    strokeWidth={isHighlighted ? edge.weight + 1 : 1}
                    opacity={isHighlighted ? 0.8 : 0.4}
                    filter={isHighlighted ? "url(#edge-glow)" : "none"}
                    markerEnd="url(#arrowhead)"
                  />
                  {isHighlighted && (
                    <text
                      x={(source.x + target.x) / 2}
                      y={(source.y + target.y) / 2 - 5}
                      textAnchor="middle"
                      className="fill-white text-xs font-medium"
                      filter="url(#glow)"
                    >
                      {edge.type}
                    </text>
                  )}
                </g>
              );
            })}

            {/* Nodes */}
            {filteredNodes.map(node => {
              const Icon = getNodeIcon(node.type);

              return (
                <g key={node.id}>
                  <circle
                    cx={node.x}
                    cy={node.y}
                    r={node.radius}
                    fill={nodeColors[node.risk]}
                    stroke={node.isSelected ? "#FFFFFF" : nodeColors[node.risk]}
                    strokeWidth={node.isSelected ? 3 : 1}
                    opacity={node.isHighlighted || !selectedNode ? 0.9 : 0.3}
                    filter={node.isSelected || node.isHighlighted ? "url(#glow)" : "none"}
                    className="cursor-pointer transition-all duration-200"
                    onClick={() => handleNodeClick(node)}
                  />

                  {/* Node Icon */}
                  <foreignObject
                    x={node.x - 8}
                    y={node.y - 8}
                    width="16"
                    height="16"
                    className="pointer-events-none"
                  >
                    <Icon className="w-4 h-4 text-white" />
                  </foreignObject>

                  {/* Node Label */}
                  <text
                    x={node.x}
                    y={node.y + node.radius + 15}
                    textAnchor="middle"
                    className="fill-white text-xs font-medium pointer-events-none"
                    opacity={node.isHighlighted || !selectedNode ? 1 : 0.5}
                  >
                    {node.label.length > 15 ? node.label.substring(0, 15) + "..." : node.label}
                  </text>

                  {/* Risk Badge */}
                  {(node.isSelected || node.isHighlighted) && (
                    <rect
                      x={node.x + node.radius - 5}
                      y={node.y - node.radius - 5}
                      width="20"
                      height="12"
                      rx="6"
                      fill={nodeColors[node.risk]}
                      opacity="0.9"
                    />
                  )}
                  {(node.isSelected || node.isHighlighted) && (
                    <text
                      x={node.x + node.radius + 5}
                      y={node.y - node.radius + 2}
                      textAnchor="middle"
                      className="fill-white text-xs font-bold"
                    >
                      {node.risk.charAt(0).toUpperCase()}
                    </text>
                  )}
                </g>
              );
            })}
          </svg>

          {/* Graph Stats Overlay */}
          <div className="absolute top-4 left-4 bg-[hsl(0,0%,10%)]/80 backdrop-blur-sm rounded-lg p-3 border border-[hsl(330,100%,50%)]/20">
            <div className="text-xs text-gray-400 space-y-1">
              <div>Nodes: <span className="text-white font-medium">{filteredNodes.length}</span></div>
              <div>Edges: <span className="text-white font-medium">{edges.length}</span></div>
              <div>Zoom: <span className="text-white font-medium">{(zoom * 100).toFixed(0)}%</span></div>
            </div>
          </div>
        </div>

        {/* Node Details Panel */}
        {selectedNode && (
          <motion.div
            initial={{ x: 300, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: 300, opacity: 0 }}
            className="w-80 border-l border-[hsl(330,100%,50%)]/20 bg-[hsl(0,0%,6%)]/60 p-4 overflow-y-auto"
          >
            <div className="flex items-center justify-between mb-4">
              <h4 className="text-lg font-semibold text-white">Node Details</h4>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setSelectedNode(null)}
                className="text-gray-400 hover:text-white"
              >
                ‚úï
              </Button>
            </div>

            <div className="space-y-4">
              <div className="flex items-center space-x-3">
                {(() => {
                  const Icon = getNodeIcon(selectedNode.type);
                  return <Icon className="w-8 h-8 text-[hsl(330,100%,50%)]" />;
                })()}
                <div>
                  <div className="text-white font-medium">{selectedNode.label}</div>
                  <div className="text-sm text-gray-400 capitalize">{selectedNode.type}</div>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Badge className={`${
                  selectedNode.risk === 'critical' ? 'bg-red-500/20 text-red-400' :
                  selectedNode.risk === 'high' ? 'bg-orange-500/20 text-orange-400' :
                  selectedNode.risk === 'medium' ? 'bg-yellow-500/20 text-yellow-400' :
                  'bg-green-500/20 text-green-400'
                }`}>
                  {selectedNode.risk.toUpperCase()}
                </Badge>
                <Badge variant="outline" className="border-[hsl(330,100%,50%)]/20 text-[hsl(330,100%,50%)]">
                  {selectedNode.connections.length} connections
                </Badge>
              </div>

              <div className="border-t border-gray-700 pt-4">
                <h5 className="text-sm font-medium text-gray-300 mb-2">Properties</h5>
                <div className="space-y-2">
                  {Object.entries(selectedNode.properties).map(([key, value]) => (
                    <div key={key} className="flex justify-between text-sm">
                      <span className="text-gray-400 capitalize">{key.replace('_', ' ')}:</span>
                      <span className="text-white">{String(value)}</span>
                    </div>
                  ))}
                </div>
              </div>

              <div className="border-t border-gray-700 pt-4">
                <h5 className="text-sm font-medium text-gray-300 mb-2">Connected Nodes</h5>
                <div className="space-y-1">
                  {selectedNode.connections.map(connId => {
                    const connectedNode = nodes.find(n => n.id === connId);
                    if (!connectedNode) return null;

                    return (
                      <div
                        key={connId}
                        className="text-sm text-gray-300 hover:text-white cursor-pointer p-2 rounded bg-[hsl(0,0%,10%)]/40 hover:bg-[hsl(0,0%,10%)]/60 transition-colors"
                        onClick={() => handleNodeClick(connectedNode)}
                      >
                        {connectedNode.label}
                        <span className="text-xs text-gray-500 ml-2">({connectedNode.type})</span>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          </motion.div>
        )}
      </div>

      {/* Legend */}
      <div className="p-4 border-t border-[hsl(330,100%,50%)]/20 bg-[hsl(0,0%,6%)]/40">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <h6 className="text-sm font-medium text-gray-300 mb-2">Risk Levels</h6>
            <div className="flex space-x-3">
              {Object.entries(nodeColors).map(([risk, color]) => (
                <div key={risk} className="flex items-center space-x-1">
                  <div className="w-3 h-3 rounded-full" style={{ backgroundColor: color }}></div>
                  <span className="text-xs text-gray-400 capitalize">{risk}</span>
                </div>
              ))}
            </div>
          </div>

          <div>
            <h6 className="text-sm font-medium text-gray-300 mb-2">Relationships</h6>
            <div className="flex flex-wrap gap-2">
              {Object.entries(edgeColors).slice(0, 4).map(([type, color]) => (
                <div key={type} className="flex items-center space-x-1">
                  <div className="w-3 h-0.5" style={{ backgroundColor: color }}></div>
                  <span className="text-xs text-gray-400">{type}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/ui/threat-visualiz.content:
"use client"

import { useState, useEffect, useRef } from "react"
import { motion, AnimatePresence } from 'framer-motion';
import { Shield, Zap, AlertTriangle, Eye, Target, Activity, Brain, TrendingUp, Clock, CheckCircle, X } from 'lucide-react';
import { Button } from './button';

interface DataSource {
  id: string
  name: string
  position: { x: number; y: number }
  isActive: boolean
  color: string
}

interface ThreatFlow {
  id: string
  sourceId: string
  progress: number
  classification: "true-positive" | "false-positive" | "manual" | null
  phase: "to-brain" | "processing" | "to-classification" | "completed"
  color: string
}

interface ClassificationStack {
  "true-positive": string[]
  "false-positive": string[]
  "manual": string[]
}

const dataSources: DataSource[] = [
  { id: "1", name: "NCFW", position: { x: 60, y: 30 }, isActive: false, color: "#3B82F6" },
  { id: "2", name: "Amazon", position: { x: 30, y: 60 }, isActive: false, color: "#10B981" },
  { id: "3", name: "Azure", position: { x: 30, y: 90 }, isActive: false, color: "#8B5CF6" },
  { id: "4", name: "Office 365", position: { x: 30, y: 120 }, isActive: false, color: "#F59E0B" },
  { id: "5", name: "SharePoint", position: { x: 30, y: 150 }, isActive: false, color: "#EF4444" },
  { id: "6", name: "Apache", position: { x: 30, y: 180 }, isActive: false, color: "#06B6D4" },
  { id: "7", name: "Prisma Cloud", position: { x: 30, y: 210 }, isActive: false, color: "#84CC16" },
]

export function ThreatVisualization() {
  const [sources, setSources] = useState<DataSource[]>(dataSources)
  const [threatFlows, setThreatFlows] = useState<ThreatFlow[]>([])
  const [classificationStacks, setClassificationStacks] = useState<ClassificationStack>({
    "true-positive": [],
    "false-positive": [],
    "manual": [],
  })
  const [rotationAngle, setRotationAngle] = useState(0)
  const [outputWires, setOutputWires] = useState({ tp: false, fp: false, manual: false })
  const [threatCount, setThreatCount] = useState(0)
  const svgRef = useRef<SVGSVGElement>(null)
  const [showAgentPopup, setShowAgentPopup] = useState(false);

  const agentAnalysisData = {
    status: "Active",
    confidence: 96.8,
    totalEvents: "2.3M",
    processedToday: 14672,
    threatsStopped: 847,
    falsePositives: 23,
    avgResponseTime: "1.2s",
    models: [
      { name: "Threat Detection Engine", accuracy: 94.2, status: "optimal" },
      { name: "Behavioral Analytics", accuracy: 91.7, status: "active" },
      { name: "ML Anomaly Detector", accuracy: 89.3, status: "learning" }
    ],
    recentActions: [
      { type: "Blocked", description: "Suspicious PowerShell execution", time: "2 min ago" },
      { type: "Quarantined", description: "Potential malware detected", time: "5 min ago" },
      { type: "Analyzed", description: "Network anomaly investigation", time: "12 min ago" }
    ]
  };

  // ===== Layout =====
  // RIGHT SIDE (unchanged)
  const truePositiveCenter = { x: 500, y: 80 }
  const falsePositiveCenter  = { x: 500, y: 200 }
  const manualCenter         = { x: 500, y: 340 }

  // Canvas width stays fixed; height will be computed dynamically
  const CANVAS_W = 620

  // Brain
  const brainCenter = { x: 260, y: 220 }

  // Left sources panel geometry
  const rowHeight = 24
  const leftPanelWidth  = 180
  const leftPanelX      = 120 - leftPanelWidth
  const leftPanelY      = 40
  const leftPanelHeight = 64 + sources.length * rowHeight + 32

  // Headings (unchanged)
  const headingBoxWidth  = 130
  const headingBoxHeight = 28
  const tpHeadingX       = truePositiveCenter.x + 12
  const tpHeadingY       = leftPanelY
  const fpHeadingX       = falsePositiveCenter.x + 12
  const fpHeadingY       = falsePositiveCenter.y - headingBoxHeight / 2
  const manualHeadingX   = manualCenter.x + 12
  const manualHeadingY   = manualCenter.y - headingBoxHeight / 2

  // ‚Äî‚Äî Bottom 4 cards: just below the lowest content ‚Äî‚Äî
  const bottomCardsX      = leftPanelX
  const bottomGap         = 16 // small visual gap to the figure above
  const figureBottom = Math.max(
    leftPanelY + leftPanelHeight,       // bottom of sources panel
    brainCenter.y + 80,                 // brain lower extent
    manualHeadingY + headingBoxHeight,  // lowest right heading bottom
  )
  const bottomCardsY      = figureBottom + bottomGap
  const bottomCardsWidth  = CANVAS_W - bottomCardsX - 20
  const bottomCardsHeight = 70

  // === Dynamic canvas/container height to remove bottom empty gap ===
  const bottomPadding     = 46
  const CANVAS_H          = bottomCardsY + bottomCardsHeight + bottomPadding
  const containerHeight   = CANVAS_H; // keeps visual size, no scaling

  // ===== Effects (logic unchanged) =====
  useEffect(() => {
    const t = setInterval(() => setRotationAngle((p) => (p + 1) % 360), 60)
    return () => clearInterval(t)
  }, [])

  useEffect(() => {
    const t = setInterval(() => {
      const randomSource = sources[Math.floor(Math.random() * sources.length)]
      const newThreat: ThreatFlow = {
        id: `threat-${Date.now()}`,
        sourceId: randomSource.id,
        progress: 0,
        classification: null,
        phase: "to-brain",
        color: randomSource.color,
      }
      setSources((prev) => prev.map((s) => (s.id === randomSource.id ? { ...s, isActive: true } : s)))
      setThreatFlows((prev) => [...prev, newThreat])
      setThreatCount((prev) => prev + 1)
      setTimeout(() => {
        setSources((prev) => prev.map((s) => (s.id === randomSource.id ? { ...s, isActive: false } : s)))
      }, 2000)
    }, 1800)
    return () => clearInterval(t)
  }, [sources])

  useEffect(() => {
    const t = setInterval(() => {
      setThreatFlows((prev) =>
        prev.map((flow) => {
          if (flow.phase === "to-brain" && flow.progress >= 100) {
            const r = Math.random()
            const classification: ThreatFlow["classification"] =
              r > 0.6 ? "true-positive" : r > 0.3 ? "false-positive" : "manual"

            if (classification === "true-positive") {
              setOutputWires((p) => ({ ...p, tp: true }))
              setTimeout(() => setOutputWires((p) => ({ ...p, tp: false })), 2000)
            } else if (classification === "false-positive") {
              setOutputWires((p) => ({ ...p, fp: true }))
              setTimeout(() => setOutputWires((p) => ({ ...p, fp: false })), 2000)
            } else {
              setOutputWires((p) => ({ ...p, manual: true }))
              setTimeout(() => setOutputWires((p) => ({ ...p, manual: false })), 2000)
            }

            return { ...flow, classification, phase: "to-classification", progress: 0 }
          }

          if (flow.phase === "to-classification" && flow.progress >= 100) {
            if (flow.classification) {
              setClassificationStacks((prevStacks) => ({
                ...prevStacks,
                [flow.classification!]: [...prevStacks[flow.classification!].slice(-4), flow.sourceId],
              }))
            }
            return { ...flow, phase: "completed" }
          }

          if (flow.phase === "completed") return flow
          return { ...flow, progress: Math.min(flow.progress + 3, 100) }
        }),
      )
    }, 50)
    return () => clearInterval(t)
  }, [])

  useEffect(() => {
    const t = setInterval(() => {
      setThreatFlows((prev) =>
        prev.filter((f) => f.phase !== "completed" || Date.now() - Number.parseInt(f.id.split("-")[1]) < 1000),
      )
    }, 1000)
    return () => clearInterval(t)
  }, [])

  // ===== Paths =====
  const getSourceWirePath = (source: DataSource) => {
    const startX = 120
    const startY = source.position.y + 60
    const endX = brainCenter.x - 45
    const endY = brainCenter.y
    const c1x = startX + 50
    const c1y = startY + (endY - startY) * 0.3
    const c2x = endX - 40
    const c2y = endY + (startY - endY) * 0.2
    return `M ${startX} ${startY} C ${c1x} ${c1y} ${c2x} ${c2y} ${endX} ${endY}`
  }

  const getOutputPath = (target: { x: number; y: number }) => {
    const startX = brainCenter.x + 45
    const startY = brainCenter.y
    const endX = target.x - 16
    const endY = target.y
    const c1x = startX + 200
    const c1y = startY + (endY - startY) * 0.6
    const c2x = endX - 40
    const c2y = endY + (startY - endY) * 0.2
    return `M ${startX} ${startY} C ${c1x} ${c1y} ${c2x} ${c2y} ${endX} ${endY}`
  }

  const renderFloatingStack = (
    center: { x: number; y: number },
    items: string[],
    color: string,
    labelMap: (id: string) => string,
  ) => {
    const max = 5
    const list = items.slice(-max)
    const startX = center.x + 18
    const startY = center.y + 22
    const step = 12
    return (
      <g>
        {list.map((id, i) => {
          const y = startY + i * step
          const text = labelMap(id)
          const w = Math.max(28, Math.min(160, text.length * 6.6))
          return (
            <g key={`${center.x}-${center.y}-${i}`} transform={`translate(${startX}, ${y})`}>
              <rect
                x={0}
                y={-8}
                rx={4}
                ry={4}
                width={w}
                height={16}
                fill="rgba(148,163,184,0.18)"
                stroke={color}
                strokeOpacity={0.45}
                strokeWidth={0.8}
              />
              <text
                x={6}
                y={4}
                fontSize="10"
                fill={color}
                style={{ fontFamily: "ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, 'Liberation Mono', 'Courier New', monospace" }}
              >
                {text}
              </text>
            </g>
          )
        })}
      </g>
    )
  }

  return (
    <div
      className="w-full bg-gradient-to-r from-[hsl(220,15%,5%)] via-[hsl(220,20%,8%)] to-[hsl(220,15%,5%)] font-mono overflow-hidden relative rounded-xl border border-[hsl(330,100%,50%)]/20"
      style={{ height: `${containerHeight}px` }}  // <-- dynamic height removes bottom gap
    >
      <svg
        ref={svgRef}
        width="100%"
        height="100%"
        viewBox={`0 0 ${CANVAS_W} ${CANVAS_H}`}  // matches dynamic canvas height
        className="w-full h-full"
      >
        <defs>
          <filter id="glow" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur stdDeviation="3" result="coloredBlur" />
            <feMerge>
              <feMergeNode in="coloredBlur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
          <filter id="strongGlow" x="-50%" y="-50%" width="200%" height="200%">
            <feGaussianBlur stdDeviation="5" result="coloredBlur" />
            <feMerge>
              <feMergeNode in="coloredBlur" />
              <feMergeNode in="SourceGraphic" />
            </feMerge>
          </filter>
          <linearGradient id="inactiveGradient" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="#374151" stopOpacity="0.3" />
            <stop offset="50%" stopColor="#4B5563" stopOpacity="0.5" />
            <stop offset="100%" stopColor="#374151" stopOpacity="0.3" />
          </linearGradient>
          <radialGradient id="brainGradient" cx="50%" cy="50%" r="50%">
            <stop offset="0%" stopColor="hsl(220,15%,5%)" />
            <stop offset="50%" stopColor="hsl(285,75%,27%)" />
            <stop offset="100%" stopColor="hsl(330,100%,50%)" />
          </radialGradient>
          <linearGradient id="outputGradient" x1="0%" y1="0%" x2="100%" y2="0%">
            <stop offset="0%" stopColor="hsl(220,15%,5%)" stopOpacity="0.8" />
            <stop offset="50%" stopColor="hsl(285,75%,27%)" stopOpacity="1" />
            <stop offset="100%" stopColor="hsl(330,100%,50%)" stopOpacity="0.8" />
          </linearGradient>
        </defs>

        {/* Input wires */}
        {sources.map((source) => {
          const wirePath = getSourceWirePath(source)
          return (
            <g key={`wire-${source.id}`}>
              <path
                d={wirePath}
                stroke={source.isActive ? source.color : "url(#inactiveGradient)"}
                strokeWidth={source.isActive ? 3 : 2}
                fill="none"
                opacity={source.isActive ? 0.9 : 0.4}
                filter={source.isActive ? "url(#glow)" : "none"}
              />
              {threatFlows
                .filter((flow) => flow.sourceId === source.id && flow.phase === "to-brain")
                .map((flow) => {
                  const progress = flow.progress / 100
                  return (
                    <circle key={flow.id} r={4} fill={flow.color} filter="url(#strongGlow)" opacity={0.9}>
                      <animateMotion
                        dur="3s"
                        begin="0s"
                        fill="freeze"
                        path={wirePath}
                        keyPoints={`${progress};${progress}`}
                        keyTimes="0;1"
                      />
                    </circle>
                  )
                })}
            </g>
          )
        })}

        {/* Output wires (right side unchanged) */}
        <path d={getOutputPath(truePositiveCenter)} stroke={outputWires.tp ? "url(#outputGradient)" : "url(#inactiveGradient)"} strokeWidth={outputWires.tp ? 4 : 2} fill="none" opacity={outputWires.tp ? 1 : 0.4} filter={outputWires.tp ? "url(#glow)" : "none"} />
        <path d={getOutputPath(falsePositiveCenter)} stroke={outputWires.fp ? "url(#outputGradient)" : "url(#inactiveGradient)"} strokeWidth={outputWires.fp ? 4 : 2} fill="none" opacity={outputWires.fp ? 1 : 0.4} filter={outputWires.fp ? "url(#glow)" : "none"} />
        <path d={getOutputPath(manualCenter)} stroke={outputWires.manual ? "url(#outputGradient)" : "url(#inactiveGradient)"} strokeWidth={outputWires.manual ? 4 : 2} fill="none" opacity={outputWires.manual ? 1 : 0.4} filter={outputWires.manual ? "url(#glow)" : "none"} />

        {/* Brain */}
        <g transform={`translate(${brainCenter.x}, ${brainCenter.y})`}>
          {Array.from({ length: 16 }).map((_, i) => {
            const base = (i * 22.5 * Math.PI) / 180
            const rot = base + (rotationAngle * Math.PI) / 180
            const z = Math.sin(rot * 2) * 0.7
            const radius = 50 + Math.sin(i * 0.8) * 5
            const x = Math.cos(rot) * radius * (1 + z * 0.3)
            const y = Math.sin(rot) * radius * (0.6 + z * 0.4)
            const scale = 1 + z * 0.5
            return <circle key={i} cx={x} cy={y} r={1.5 * scale} fill="hsl(330,100%,50%)" opacity={0.6 + z * 0.3} filter="url(#glow)" />
          })}
          <circle cx="0" cy="0" r="40" fill="url(#brainGradient)" stroke="hsl(330,100%,50%)" strokeWidth="2" filter="url(#strongGlow)" opacity="0.8" />
