          {/* Security Status Indicator */}
          <div className="absolute top-2 right-2">
            <motion.div
              animate={{ 
                backgroundColor: loginStep === 3 ? "#10b981" : loginStep >= 1 ? "#f59e0b" : "#ef4444",
